http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "field": {
            "type": "object",
            "description": "Details of the custom field to be updated.",
            "properties": {
              "description": {
                "type": "string",
                "nullable": true,
                "description": "A description of the data this field contains.",
                "maxLength": 1000
              },
              "display_name": {
                "type": "string",
                "description": "The human-readable name of the field. This must be unique across an account.",
                "maxLength": 50
              },
              "enabled": {
                "type": "boolean",
                "description": "Whether the field is enabled.",
                "enum": [
                  true,
                  false
                ]
              },
              "field_options": {
                "type": "array",
                "items": {
                  "allOf": [
                    {
                      "type": "object",
                      "description": "An option for a custom field. Can only be applied to fields with a `field_type` of `single_value_fixed` or `multi_value_fixed`.",
                      "properties": {
                        "data": {
                          "type": "object",
                          "description": "The data content of the field option.",
                          "properties": {
                            "data_type": {
                              "type": "string",
                              "description": "The kind of data represented by this option. Must match the Field's `data_type`.",
                              "enum": [
                                "string"
                              ]
                            },
                            "value": {
                              "type": "string",
                              "description": "The value of the field option. Must be unique within the field.",
                              "maxLength": 200
                            }
                          },
                          "required": [
                            "data_type",
                            "value"
                          ]
                        }
                      },
                      "required": [
                        "data"
                      ]
                    },
                    {
                      "type": "object",
                      "properties": {
                        "id": {
                          "type": "string",
                          "description": "The unique identifier of the field option. How this field is used determines the behavior:\n  - When included with a valid `id`: Updates the corresponding existing field option\n  - When omitted or null: Creates a new field option\n"
                        }
                      }
                    }
                  ]
                },
                "description": "List of field options to update, insert or delete. This field supports several behaviors:\n  - Empty array: Deletes all field options\n  - Omitting the `field_options` array entirely: Preserves all existing options\n  - Not listing an existing option: Deletes that option (unless it's the current default value)\n"
              }
            }
          }
        },
        "required": [
          "field"
        ]
      },
      "examples": {
        "request": {
          "summary": "Update Field Example - Basic Properties",
          "value": {
            "field": {
              "description": "The production environment where this service is deployed",
              "display_name": "Production Environment",
              "enabled": true
            }
          }
        },
        "request_with_options": {
          "summary": "Update Field Example - With Field Options",
          "value": {
            "field": {
              "description": "The production environment where this service is deployed",
              "display_name": "Production Environment",
              "enabled": true,
              "field_options": [
                {
                  "data": {
                    "data_type": "string",
                    "value": "production"
                  },
                  "id": "OPT1"
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "staging-new"
                  },
                  "id": "OPT2"
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "development-new"
                  }
                }
              ]
            }
          }
        }
      }
    }
  }
}
