http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "session_configuration": {
            "type": "object",
            "properties": {
              "absolute_session_ttl": {
                "type": "integer",
                "description": "Absolute session time to live in seconds",
                "example": 3600,
                "minimum": 600,
                "maximum": 18144000
              },
              "idle_session_ttl": {
                "type": "integer",
                "description": "Idle session time to live in seconds",
                "example": 600,
                "minimum": 60,
                "maximum": 86400
              }
            },
            "required": [
              "absolute_session_ttl",
              "idle_session_ttl"
            ]
          }
        },
        "required": [
          "session_configuration"
        ]
      }
    }
  }
}
