http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "template": {
            "type": "object",
            "properties": {
              "template_type": {
                "type": "string",
                "description": "The type of template (`status_update` is the only supported template at this time)",
                "enum": [
                  "status_update"
                ]
              },
              "name": {
                "type": "string",
                "description": "The name of the template"
              },
              "description": {
                "type": "string",
                "nullable": true,
                "description": "Description of the template"
              },
              "templated_fields": {
                "type": "object",
                "properties": {
                  "email_subject": {
                    "type": "string",
                    "nullable": true,
                    "description": "The subject of the e-mail"
                  },
                  "email_body": {
                    "type": "string",
                    "nullable": true,
                    "description": "The HTML body of the e-mail message"
                  },
                  "message": {
                    "type": "string",
                    "nullable": true,
                    "description": "The short-message of the template (SMS, Push notification, Slack,\netc)"
                  }
                }
              }
            }
          }
        },
        "required": [
          "template"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "template": {
              "description": "Sample template description",
              "templated_fields": {
                "email_body": "<div> sample </div>",
                "email_subject": "Sample email Subject",
                "message": "Sample SMS message"
              },
              "name": "Sample Template",
              "template_type": "status_update"
            }
          }
        }
      }
    }
  },
  "required": true
}
