                          },
                          "html_url": {
                            "type": "string",
                            "nullable": true,
                            "readOnly": true,
                            "format": "url",
                            "description": "a URL at which the entity is uniquely displayed in the Web app"
                          }
                        }
                      },
                      {
                        "type": "object",
