                          }
                        },
                        "discriminator": {
                          "propertyName": "type"
                        },
                        "required": [
                          "type",
                          "label",
                          "address"
                        ]
                      }
                    ]
                  },
                  {
                    "type": "object",
                    "properties": {
                      "type": {
                        "type": "string",
                        "enum": [
                          "push_notification_contact_method"
                        ]
                      },
                      "device_type": {
                        "type": "string",
                        "description": "The type of device.",
                        "enum": [
                          "android",
                          "ios"
                        ],
                        "readOnly": true
                      },
                      "sounds": {
                        "type": "array",
                        "items": {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "description": "The type of sound.",
                              "enum": [
                                "alert_high_urgency",
                                "alert_low_urgency"
                              ]
                            },
                            "file": {
                              "type": "string",
                              "description": "The sound file name."
                            }
                          }
                        }
                      },
                      "created_at": {
                        "type": "string",
                        "format": "date-time",
                        "description": "Time at which the contact method was created."
                      },
                      "blacklisted": {
                        "type": "boolean",
                        "description": "If true, this phone has been blacklisted by PagerDuty and no messages will be sent to it.",
                        "readOnly": true
                      }
                    },
                    "required": [
                      "device_type"
                    ],
                    "example": {
                      "type": "push_notification_contact_method",
                      "label": "work",
                      "device_type": "android",
                      "address": "12341234"
                    }
                  }
                ]
              },
