http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "notification_rule": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "summary": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                  },
                  "type": {
                    "type": "string",
                    "readOnly": true,
                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                  },
                  "self": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "the API show URL at which the object is accessible"
                  },
                  "html_url": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                  }
                }
              },
              {
                "type": "object",
                "description": "A rule for contacting the user.",
                "properties": {
                  "type": {
                    "type": "string",
                    "description": "The type of object being created.",
                    "default": "assignment_notification_rule",
                    "enum": [
                      "assignment_notification_rule"
                    ]
                  },
                  "start_delay_in_minutes": {
                    "type": "integer",
                    "description": "The delay before firing the rule, in minutes.",
                    "minimum": 0
                  },
                  "contact_method": {
                    "allOf": [
                      {
                        "allOf": [
                          {
                            "type": "object",
                            "properties": {
                              "id": {
                                "type": "string",
                                "readOnly": true
                              },
                              "summary": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                              },
                              "type": {
                                "type": "string",
                                "readOnly": true,
                                "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                              },
                              "self": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "format": "url",
                                "description": "the API show URL at which the object is accessible"
                              },
                              "html_url": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "format": "url",
                                "description": "a URL at which the entity is uniquely displayed in the Web app"
                              }
                            }
                          },
                          {
                            "type": "object",
                            "required": [
                              "type",
                              "id"
                            ]
                          }
                        ]
                      },
                      {
                        "type": "object",
                        "properties": {
                          "type": {
                            "type": "string",
                            "enum": [
                              "email_contact_method_reference",
                              "phone_contact_method_reference",
                              "push_notification_contact_method_reference",
                              "sms_contact_method_reference"
                            ]
                          }
                        }
                      }
                    ]
                  },
                  "urgency": {
                    "type": "string",
                    "enum": [
                      "high",
                      "low"
                    ],
                    "description": "Which incident urgency this rule is used for. Account must have the `urgencies` ability to have a low urgency notification rule."
                  }
                },
                "required": [
                  "start_delay_in_minutes",
                  "urgency",
                  "contact_method",
                  "type"
                ],
                "example": {
                  "type": "assignment_notification_rule",
                  "start_delay_in_minutes": 0,
                  "contact_method": {
                    "id": "PXPGF42",
                    "type": "email_contact_method_reference"
                  },
                  "urgency": "high"
                }
              }
            ]
          }
        },
        "required": [
          "notification_rule"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "notification_rule": {
              "type": "assignment_notification_rule",
              "start_delay_in_minutes": 0,
              "contact_method": {
                "id": "PXPGF42",
                "type": "email_contact_method_reference"
              },
              "urgency": "high"
            }
          }
        }
      }
    }
  },
  "description": "The user's notification rule to be updated."
}
