http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Simplified serializer to speed response times when loading large amounts of objects.",
        "properties": {
          "id": {
            "type": "integer",
            "readOnly": true
          },
          "short_id": {
            "type": "string",
            "readOnly": true
          },
          "name": {
            "type": "string",
            "nullable": true,
            "maxLength": 400
          },
          "derived_name": {
            "type": "string",
            "nullable": true,
            "maxLength": 400
          },
          "query": {
            "type": "object",
            "example": {
              "kind": "InsightVizNode",
              "source": {
                "kind": "TrendsQuery",
                "series": [
                  {
                    "kind": "EventsNode",
                    "math": "total",
                    "name": "$pageview",
                    "event": "$pageview",
                    "version": 1
                  }
                ],
                "version": 1
              },
              "version": 1
            },
            "nullable": true
          },
          "order": {
            "type": "integer",
            "maximum": 2147483647,
            "minimum": -2147483648,
            "nullable": true
          },
          "deleted": {
            "type": "boolean"
          },
          "dashboards": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "\n        DEPRECATED. Will be removed in a future release. Use dashboard_tiles instead.\n        A dashboard ID for each of the dashboards that this insight is displayed on.\n        "
          },
          "dashboard_tiles": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "type": "integer",
                  "readOnly": true
                },
                "dashboard_id": {
                  "type": "integer",
                  "readOnly": true
                },
                "deleted": {
                  "type": "boolean",
                  "nullable": true
                }
              },
              "required": [
                "dashboard_id",
                "id"
              ]
            },
            "readOnly": true,
            "description": "\n    A dashboard tile ID and dashboard_id for each of the dashboards that this insight is displayed on.\n    "
          },
          "last_refresh": {
            "type": "string",
            "readOnly": true,
            "description": "\n    The datetime this insight's results were generated.\n    If added to one or more dashboards the insight can be refreshed separately on each.\n    Returns the appropriate last_refresh datetime for the context the insight is viewed in\n    (see from_dashboard query parameter).\n    "
          },
          "cache_target_age": {
            "type": "string",
            "readOnly": true,
            "description": "The target age of the cached results for this insight."
          },
          "next_allowed_client_refresh": {
            "type": "string",
            "readOnly": true,
            "description": "\n    The earliest possible datetime at which we'll allow the cached results for this insight to be refreshed\n    by querying the database.\n    "
          },
          "result": {
            "type": "string",
            "readOnly": true
          },
          "hasMore": {
            "type": "string",
            "readOnly": true
          },
          "columns": {
            "type": "string",
            "readOnly": true
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true,
            "nullable": true
          },
          "created_by": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "integer",
                    "readOnly": true
                  },
                  "uuid": {
                    "type": "string",
                    "format": "uuid",
                    "readOnly": true
                  },
                  "distinct_id": {
                    "type": "string",
                    "nullable": true,
                    "maxLength": 200
                  },
                  "first_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "last_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "email": {
                    "type": "string",
                    "format": "email",
                    "title": "Email address",
                    "maxLength": 254
                  },
                  "is_email_verified": {
                    "type": "boolean",
                    "nullable": true
                  },
                  "hedgehog_config": {
                    "type": "object",
                    "additionalProperties": {},
                    "nullable": true,
                    "readOnly": true
                  },
                  "role_at_organization": {
                    "nullable": true,
                    "oneOf": [
                      {
                        "enum": [
                          "engineering",
                          "data",
                          "product",
                          "founder",
                          "leadership",
                          "marketing",
                          "sales",
                          "other"
                        ],
                        "type": "string",
                        "description": "* `engineering` - Engineering\n* `data` - Data\n* `product` - Product Management\n* `founder` - Founder\n* `leadership` - Leadership\n* `marketing` - Marketing\n* `sales` - Sales / Success\n* `other` - Other"
                      },
                      {
                        "enum": [
                          ""
                        ]
                      },
                      {
                        "enum": [
                          null
                        ]
                      }
                    ]
                  }
                },
                "required": [
                  "email",
                  "hedgehog_config",
                  "id",
                  "uuid"
                ]
              }
            ],
            "readOnly": true
          },
          "description": {
            "type": "string",
            "nullable": true,
            "maxLength": 400
          },
          "updated_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true
          },
          "tags": {
            "type": "array",
            "items": {}
          },
          "favorited": {
            "type": "boolean"
          },
          "last_modified_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true
          },
          "last_modified_by": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "integer",
                    "readOnly": true
                  },
                  "uuid": {
                    "type": "string",
                    "format": "uuid",
                    "readOnly": true
                  },
                  "distinct_id": {
                    "type": "string",
                    "nullable": true,
                    "maxLength": 200
                  },
                  "first_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "last_name": {
                    "type": "string",
                    "maxLength": 150
                  },
                  "email": {
                    "type": "string",
                    "format": "email",
                    "title": "Email address",
                    "maxLength": 254
                  },
                  "is_email_verified": {
                    "type": "boolean",
                    "nullable": true
                  },
                  "hedgehog_config": {
                    "type": "object",
                    "additionalProperties": {},
                    "nullable": true,
                    "readOnly": true
                  },
                  "role_at_organization": {
                    "nullable": true,
                    "oneOf": [
                      {
                        "enum": [
                          "engineering",
                          "data",
                          "product",
                          "founder",
                          "leadership",
                          "marketing",
                          "sales",
                          "other"
                        ],
                        "type": "string",
                        "description": "* `engineering` - Engineering\n* `data` - Data\n* `product` - Product Management\n* `founder` - Founder\n* `leadership` - Leadership\n* `marketing` - Marketing\n* `sales` - Sales / Success\n* `other` - Other"
                      },
                      {
                        "enum": [
                          ""
                        ]
                      },
                      {
                        "enum": [
                          null
                        ]
                      }
                    ]
                  }
                },
                "required": [
                  "email",
                  "hedgehog_config",
                  "id",
                  "uuid"
                ]
              }
            ],
            "readOnly": true
          },
          "is_sample": {
            "type": "boolean",
            "readOnly": true
          },
          "effective_restriction_level": {
            "allOf": [
              {
                "enum": [
                  21,
                  37
                ],
                "type": "integer"
              }
            ],
            "readOnly": true
          },
          "effective_privilege_level": {
            "allOf": [
              {
                "enum": [
                  21,
                  37
                ],
                "type": "integer"
              }
            ],
            "readOnly": true
          },
          "user_access_level": {
            "type": "string",
            "nullable": true,
            "readOnly": true,
            "description": "The effective access level the user has for this object"
          },
          "timezone": {
            "type": "string",
            "readOnly": true,
            "description": "The timezone this chart is displayed in."
          },
          "is_cached": {
            "type": "string",
            "readOnly": true
          },
          "query_status": {
            "type": "string",
            "readOnly": true
          },
          "hogql": {
            "type": "string",
            "readOnly": true
          },
          "types": {
            "type": "string",
            "readOnly": true
          },
          "_create_in_folder": {
            "type": "string",
            "writeOnly": true,
            "title": " create in folder"
          },
          "alerts": {
            "type": "string",
            "readOnly": true
          },
          "last_viewed_at": {
            "type": "string",
            "readOnly": true
          }
        },
        "required": [
          "alerts",
          "cache_target_age",
          "columns",
          "created_at",
          "created_by",
          "dashboard_tiles",
          "effective_privilege_level",
          "effective_restriction_level",
          "hasMore",
          "hogql",
          "id",
          "is_cached",
          "is_sample",
          "last_modified_at",
          "last_modified_by",
          "last_refresh",
          "last_viewed_at",
          "next_allowed_client_refresh",
          "query_status",
          "result",
          "short_id",
          "timezone",
          "types",
          "updated_at",
          "user_access_level"
        ]
      }
    }
  }
}
