{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "string",
            "format": "uuid",
            "readOnly": true
          },
          "task": {
            "type": "string",
            "format": "uuid",
            "readOnly": true
          },
          "stage": {
            "type": "string",
            "nullable": true,
            "description": "Current stage for this run (e.g., 'backlog', 'in_progress', 'done')",
            "maxLength": 100
          },
          "branch": {
            "type": "string",
            "nullable": true,
            "description": "Branch name for the run",
            "maxLength": 255
          },
          "status": {
            "enum": [
              "started",
              "in_progress",
              "completed",
              "failed"
            ],
            "type": "string",
            "description": "* `started` - Started\n* `in_progress` - In Progress\n* `completed` - Completed\n* `failed` - Failed"
          },
          "log": {
            "description": "Live output from Claude Code execution"
          },
          "error_message": {
            "type": "string",
            "nullable": true,
            "description": "Error message if execution failed"
          },
          "output": {
            "nullable": true,
            "description": "Run output data (e.g., PR URL, commit SHA, etc.)"
          },
          "state": {
            "description": "Run state data for resuming or tracking execution state"
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true
          },
          "updated_at": {
            "type": "string",
            "format": "date-time",
            "readOnly": true
          },
