http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "attachment_urls": {
            "description": "An array of attachment URLs to attach to this issue.",
            "items": {
              "type": "string"
            },
            "type": "array",
            "x-go-name": "AttachmentURLs"
          },
          "body_html": {
            "description": "The body of the message in HTML.",
            "type": "string",
            "x-go-name": "BodyHTML"
          },
          "contact_id": {
            "description": "Optional contact ID to post the message as. Only one of user_id or contact_id can be provided.",
            "type": "string",
            "x-go-name": "ContactID"
          },
          "email_info": {
            "properties": {
              "bcc_emails": {
                "items": {
                  "type": "string"
                },
                "type": "array",
                "x-go-name": "BccEmails"
              },
              "cc_emails": {
                "items": {
                  "type": "string"
                },
                "type": "array",
                "x-go-name": "CcEmails"
              },
              "to_emails": {
                "items": {
                  "type": "string"
                },
                "type": "array",
                "x-go-name": "ToEmails"
              }
            },
            "type": "object",
            "x-go-package": "pylon/api/apiserver/endpoints"
          },
          "message_id": {
            "description": "The ID of the message to reply to.",
            "type": "string",
            "x-go-name": "MessageID"
          },
          "user_id": {
            "description": "Optional user ID to post the message as. Only one of user_id or contact_id can be provided.",
            "type": "string",
            "x-go-name": "UserID"
          }
        },
        "required": [
          "body_html",
          "message_id"
        ],
        "type": "object",
        "x-go-package": "pylon/api/apiserver/endpoints"
      }
    }
  },
  "required": false
}
