http_endpoint.request_body_spec:
{
  "required": true,
  "description": "An object containing the fields to POST for the request.\n\nSee the corresponding object definition for field details.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Request to create a new transfer order.",
        "x-release-status": "BETA",
        "required": [
          "idempotency_key",
          "transfer_order"
        ],
        "properties": {
          "idempotency_key": {
            "type": "string",
            "description": "A unique string that identifies this CreateTransferOrder request. Keys can be\nany valid string but must be unique for every CreateTransferOrder request.",
            "minLength": 1
          },
          "transfer_order": {
            "type": "object",
            "description": "Data for creating a new transfer order to move [CatalogItemVariation](entity:CatalogItemVariation)s\nbetween [Location](entity:Location)s. Used with the [CreateTransferOrder](api-endpoint:TransferOrders-CreateTransferOrder)\nendpoint.",
            "x-release-status": "BETA",
            "required": [
              "source_location_id",
              "destination_location_id"
            ],
            "properties": {
              "source_location_id": {
                "type": "string",
                "description": "The source [Location](entity:Location) that will send the items. Must be an active location\nin your Square account with sufficient inventory of the requested items.",
                "minLength": 1,
                "maxLength": 64
              },
              "destination_location_id": {
                "type": "string",
                "description": "The destination [Location](entity:Location) that will receive the items. Must be an active location\nin your Square account",
                "minLength": 1,
                "maxLength": 64
              },
              "expected_at": {
                "type": "string",
                "description": "Expected transfer date in RFC 3339 format (e.g. \"2023-10-01T12:00:00Z\").",
                "nullable": true
              },
              "notes": {
                "type": "string",
                "description": "Optional notes about the transfer",
                "maxLength": 4096,
                "nullable": true
              },
              "tracking_number": {
                "type": "string",
                "description": "Optional shipment tracking number",
                "maxLength": 64,
                "nullable": true
              },
              "created_by_team_member_id": {
                "type": "string",
                "description": "ID of the [TeamMember](entity:TeamMember) creating this transfer order. Used for tracking\nand auditing purposes.",
                "maxLength": 64,
                "nullable": true
              },
              "line_items": {
                "type": "array",
                "items": {
                  "type": "object",
                  "description": "Data for creating a new transfer order line item. Each line item specifies a \n[CatalogItemVariation](entity:CatalogItemVariation) and quantity to transfer.",
                  "x-release-status": "BETA",
                  "required": [
                    "item_variation_id",
                    "quantity_ordered"
                  ],
                  "properties": {
                    "item_variation_id": {
                      "type": "string",
                      "description": "ID of the [CatalogItemVariation](entity:CatalogItemVariation) to transfer. Must reference a valid\nitem variation in the [Catalog](api:Catalog). The item variation must be:\n- Active and available for sale\n- Enabled for inventory tracking\n- Available at the source location",
                      "minLength": 1,
                      "maxLength": 64
                    },
                    "quantity_ordered": {
                      "type": "string",
                      "description": "Total quantity ordered",
                      "minLength": 1,
                      "maxLength": 16,
                      "pattern": "^(0|[1-9]\\d*)(\\.\\d{1,4})?$"
                    }
                  }
                },
                "description": "List of [CatalogItemVariation](entity:CatalogItemVariation)s to transfer, including quantities",
                "nullable": true
              }
            }
          }
        },
        "example": {
          "idempotency_key": "65cc0586-3e82-384s-b524-3885cffd52",
          "transfer_order": {
            "created_by_team_member_id": "EXAMPLE_TEAM_MEMBER_ID_789",
            "destination_location_id": "EXAMPLE_DEST_LOCATION_ID_456",
            "expected_at": "2025-11-09T05:00:00Z",
            "line_items": [
              {
                "item_variation_id": "EXAMPLE_ITEM_VARIATION_ID_001",
                "quantity_ordered": "5"
              },
              {
                "item_variation_id": "EXAMPLE_ITEM_VARIATION_ID_002",
                "quantity_ordered": "3"
              }
            ],
            "notes": "Example transfer order for inventory redistribution between locations",
            "source_location_id": "EXAMPLE_SOURCE_LOCATION_ID_123",
            "tracking_number": "TRACK123456789"
          }
        }
      }
    }
  }
}
