http_endpoint.request_body_spec:
{
  "required": true,
  "description": "An object containing the fields to POST for the request.\n\nSee the corresponding object definition for field details.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Represents a [SearchScheduledShift](api-endpoint:Labor-SearchScheduledShift) request.",
        "x-release-status": "BETA",
        "properties": {
          "query": {
            "type": "object",
            "description": "Represents filter and sort criteria for the `query` field in a\n[SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts) request.",
            "x-release-status": "BETA",
            "properties": {
              "filter": {
                "type": "object",
                "description": "Defines filter criteria for a [SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts)\nrequest. Multiple filters in a query are combined as an `AND` operation.",
                "x-release-status": "BETA",
                "properties": {
                  "location_ids": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "description": "Return shifts for the specified locations. When omitted, shifts for all\nlocations are returned. If needed, call [ListLocations](api-endpoint:Locations-ListLocations)\nto get location IDs.",
                    "nullable": true
                  },
                  "start": {
                    "type": "object",
                    "description": "Represents a generic time range. The start and end values are\nrepresented in RFC 3339 format. Time ranges are customized to be\ninclusive or exclusive based on the needs of a particular endpoint.\nRefer to the relevant endpoint-specific documentation to determine\nhow time ranges are handled.",
                    "x-release-status": "PUBLIC",
                    "properties": {
                      "start_at": {
                        "type": "string",
                        "description": "A datetime value in RFC 3339 format indicating when the time range\nstarts.",
                        "nullable": true
                      },
                      "end_at": {
                        "type": "string",
                        "description": "A datetime value in RFC 3339 format indicating when the time range\nends.",
                        "nullable": true
                      }
                    }
                  },
                  "end": {
                    "type": "object",
                    "description": "Represents a generic time range. The start and end values are\nrepresented in RFC 3339 format. Time ranges are customized to be\ninclusive or exclusive based on the needs of a particular endpoint.\nRefer to the relevant endpoint-specific documentation to determine\nhow time ranges are handled.",
                    "x-release-status": "PUBLIC",
                    "properties": {
                      "start_at": {
                        "type": "string",
                        "description": "A datetime value in RFC 3339 format indicating when the time range\nstarts.",
                        "nullable": true
                      },
                      "end_at": {
                        "type": "string",
                        "description": "A datetime value in RFC 3339 format indicating when the time range\nends.",
                        "nullable": true
                      }
                    }
                  },
                  "workday": {
                    "type": "object",
                    "description": "A `ScheduledShift` search query filter parameter that sets a range of days that\na `Shift` must start or end in before passing the filter condition.",
                    "x-release-status": "BETA",
                    "properties": {
                      "date_range": {
                        "type": "object",
                        "description": "A range defined by two dates. Used for filtering a query for Connect v2\nobjects that have date properties.",
                        "x-release-status": "PUBLIC",
                        "properties": {
                          "start_date": {
                            "type": "string",
                            "description": "A string in `YYYY-MM-DD` format, such as `2017-10-31`, per the ISO 8601\nextended format for calendar dates.\nThe beginning of a date range (inclusive).",
                            "nullable": true
                          },
                          "end_date": {
                            "type": "string",
                            "description": "A string in `YYYY-MM-DD` format, such as `2017-10-31`, per the ISO 8601\nextended format for calendar dates.\nThe end of a date range (inclusive).",
                            "nullable": true
                          }
                        }
                      },
                      "match_scheduled_shifts_by": {
                        "type": "string",
                        "enum": [
                          "START_AT",
                          "END_AT",
                          "INTERSECTION"
                        ],
                        "x-enum-elements": [
                          {
                            "name": "START_AT",
                            "description": "All shifts that start on or after the specified workday"
                          },
                          {
                            "name": "END_AT",
                            "description": "All shifts that end on or before the specified workday"
                          },
                          {
                            "name": "INTERSECTION",
                            "description": "All shifts that start between the start and end workdays (inclusive)"
                          }
                        ],
                        "description": "Defines the logic used to apply a workday filter.",
                        "x-release-status": "BETA"
                      },
                      "default_timezone": {
                        "type": "string",
                        "description": "Location-specific timezones convert workdays to datetime filters.\nEvery location included in the query must have a timezone or this field\nmust be provided as a fallback. Format: the IANA timezone database\nidentifier for the relevant timezone.",
                        "nullable": true
                      }
                    }
                  },
                  "team_member_ids": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "description": "Return shifts assigned to specified team members. If needed, call\n[SearchTeamMembers](api-endpoint:Team-SearchTeamMembers) to get team member IDs.\n\nTo return only the shifts assigned to the specified team members, include the\n`assignment_status` filter in the query. Otherwise, all unassigned shifts are\nreturned along with shifts assigned to the specified team members.",
                    "nullable": true
                  },
                  "assignment_status": {
                    "type": "string",
                    "enum": [
                      "ASSIGNED",
                      "UNASSIGNED"
                    ],
                    "x-enum-elements": [
                      {
                        "name": "ASSIGNED",
                        "description": "The shift is assigned to a team member."
                      },
                      {
                        "name": "UNASSIGNED",
                        "description": "The shift isn't assigned to a team member."
                      }
                    ],
                    "description": "Defines valid values for the `assignment_status` filter in a\n[SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts) request.\nAssignment status is based on the `draft_shift_details.team_member_id` and\n`published_shift_details.team_member_id` fields of the scheduled shift.",
                    "x-release-status": "BETA"
                  },
                  "scheduled_shift_statuses": {
                    "type": "array",
                    "items": {
                      "type": "string",
                      "enum": [
                        "DRAFT",
                        "PUBLISHED"
                      ],
                      "x-enum-elements": [
                        {
                          "name": "DRAFT",
                          "description": "The scheduled shift has never been published or its draft details were\nupdated after it was published. This means that the shift has no `published_shift_details`\nfield or that `draft_shift_details` has different settings than `published_shift_details`."
                        },
                        {
                          "name": "PUBLISHED",
                          "description": "The scheduled shift was published. This means that the shift has a\n`published_shift_details` field."
                        }
                      ],
                      "description": "Defines valid values for the `scheduled_shift_statuses` filter in a\n[SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts) request.",
                      "x-release-status": "BETA"
                    },
                    "description": "Return shifts based on the draft or published status of the shift.\nA shift is published if the `published_shift_details` field is present.\n\nNote that shifts with `draft_shift_details.is_deleted` set to `true` are ignored\nwith the `DRAFT` filter.\nSee [ScheduledShiftFilterScheduledShiftStatus](#type-scheduledshiftfilterscheduledshiftstatus) for possible values",
                    "nullable": true
                  }
                }
              },
              "sort": {
                "type": "object",
                "description": "Defines sort criteria for a [SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts)\nrequest.",
                "x-release-status": "BETA",
                "properties": {
                  "field": {
                    "type": "string",
                    "enum": [
                      "START_AT",
                      "END_AT",
                      "CREATED_AT",
                      "UPDATED_AT"
                    ],
                    "x-enum-elements": [
                      {
                        "name": "START_AT",
                        "description": "Sort by the start time of the shift."
                      },
                      {
                        "name": "END_AT",
                        "description": "Sort by the end time of the shift."
                      },
                      {
                        "name": "CREATED_AT",
                        "description": "Sort by the creation date of the shift."
                      },
                      {
                        "name": "UPDATED_AT",
                        "description": "Sort by the last updated date of the shift."
                      }
                    ],
                    "description": "Defines valid values for the `field` sort setting in a\n[SearchScheduledShifts](api-endpoint:Labor-SearchScheduledShifts) request.",
                    "x-release-status": "BETA"
                  },
                  "order": {
                    "type": "string",
                    "enum": [
                      "DESC",
                      "ASC"
                    ],
                    "x-enum-elements": [
                      {
                        "name": "DESC",
                        "description": "The results are returned in descending (e.g., newest-first or Z-A) order."
                      },
                      {
                        "name": "ASC",
                        "description": "The results are returned in ascending (e.g., oldest-first or A-Z) order."
                      }
                    ],
                    "description": "The order (e.g., chronological or alphabetical) in which results from a request are returned.",
                    "x-release-status": "PUBLIC"
                  }
                }
              }
            }
          },
          "limit": {
            "type": "integer",
            "description": "The maximum number of results to return in a single response page. The default value is 50.",
            "minimum": 1,
            "maximum": 50
          },
          "cursor": {
            "type": "string",
            "description": "The pagination cursor returned by the previous call to this endpoint. Provide\nthis cursor to retrieve the next page of results for your original request. For more\ninformation, see [Pagination](https://developer.squareup.com/docs/build-basics/common-api-patterns/pagination)."
          }
        },
        "example": {
          "cursor": "xoxp-1234-5678-90123",
          "limit": 2,
          "query": {
            "filter": {
              "assignment_status": "ASSIGNED",
              "scheduled_shift_status": [
                "PUBLISHED",
                "DRAFT"
              ]
            },
            "sort": {
              "field": "CREATED_AT",
              "order": "ASC"
            }
          }
        }
      }
    }
  }
}
