http_endpoint.request_body_spec:
{
  "required": true,
  "description": "An object containing the fields to POST for the request.\n\nSee the corresponding object definition for field details.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Request to search transfer orders",
        "x-release-status": "BETA",
        "properties": {
          "query": {
            "type": "object",
            "description": "Query parameters for searching transfer orders",
            "x-release-status": "BETA",
            "properties": {
              "filter": {
                "type": "object",
                "description": "Filter criteria for searching transfer orders",
                "x-release-status": "BETA",
                "properties": {
                  "source_location_ids": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "description": "Filter by source location IDs",
                    "nullable": true
                  },
                  "destination_location_ids": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    },
                    "description": "Filter by destination location IDs",
                    "nullable": true
                  },
                  "statuses": {
                    "type": "array",
                    "items": {
                      "type": "string",
                      "enum": [
                        "DRAFT",
                        "STARTED",
                        "PARTIALLY_RECEIVED",
                        "COMPLETED",
                        "CANCELED"
                      ],
                      "x-enum-elements": [
                        {
                          "name": "DRAFT",
                          "description": "Initial status when transfer order is created via [CreateTransferOrder](api-endpoint:TransferOrders-CreateTransferOrder).\nOrder can be modified or deleted. No inventory impact at this stage."
                        },
                        {
                          "name": "STARTED",
                          "description": "Transfer order has been started via [StartTransferOrder](api-endpoint:TransferOrders-StartTransferOrder).\nOrder can no longer be deleted. [CatalogItemVariation](entity:CatalogItemVariation)s are decremented \nfrom source [Location](entity:Location) and marked as in-transit to destination."
                        },
                        {
                          "name": "PARTIALLY_RECEIVED",
                          "description": "Some items have been received via [ReceiveTransferOrder](api-endpoint:TransferOrders-ReceiveTransferOrder)\nbut there are still pending quantities. Additional receipts are allowed.\n[CatalogItemVariation](entity:CatalogItemVariation)s are incrementally added to destination \n[Location](entity:Location) inventory as they are received."
                        },
                        {
                          "name": "COMPLETED",
                          "description": "All items have been received or canceled, no pending quantities remain. \nOnly metadata modifications are allowed. Final inventory adjustments are complete\nat both source and destination [Location](entity:Location)s."
                        },
                        {
                          "name": "CANCELED",
                          "description": "Transfer order was canceled via [CancelTransferOrder](api-endpoint:TransferOrders-CancelTransferOrder).\nAny pending quantities are no longer receivable and are returned to source [Location](entity:Location). \nOnly metadata modifications are allowed."
                        }
                      ],
                      "description": "Status values for transfer orders. Represents the current state of a \n[TransferOrder](entity:TransferOrder) in its lifecycle from creation to completion.\nEach status determines what actions are available and how inventory is affected.",
                      "x-release-status": "BETA"
                    },
                    "description": "Filter by order statuses\nSee [TransferOrderStatus](#type-transferorderstatus) for possible values",
                    "nullable": true
                  }
                }
              },
              "sort": {
                "type": "object",
                "description": "Sort configuration for search results",
                "x-release-status": "BETA",
                "properties": {
                  "field": {
                    "type": "string",
                    "enum": [
                      "CREATED_AT",
                      "UPDATED_AT"
                    ],
                    "x-enum-elements": [
                      {
                        "name": "CREATED_AT",
                        "description": "Sort by creation date (created_at field). Useful for:\n- Finding newest transfers\n- Chronological order processing\n- Historical analysis\n- Auditing transfer patterns"
                      },
                      {
                        "name": "UPDATED_AT",
                        "description": "Sort by last update date (updated_at field). Useful for:\n- Finding recently modified transfers\n- Tracking status changes\n- Monitoring receiving progress\n- Synchronizing with external systems"
                      }
                    ],
                    "description": "Fields that can be used for sorting [TransferOrder](entity:TransferOrder)s in search results.\nUsed with [SearchTransferOrders](api-endpoint:TransferOrders-SearchTransferOrders) to control\nthe order of returned results.",
                    "x-release-status": "BETA"
                  },
                  "order": {
                    "type": "string",
                    "enum": [
                      "DESC",
                      "ASC"
                    ],
                    "x-enum-elements": [
                      {
                        "name": "DESC",
                        "description": "The results are returned in descending (e.g., newest-first or Z-A) order."
                      },
                      {
                        "name": "ASC",
                        "description": "The results are returned in ascending (e.g., oldest-first or A-Z) order."
                      }
                    ],
                    "description": "The order (e.g., chronological or alphabetical) in which results from a request are returned.",
                    "x-release-status": "PUBLIC"
                  }
                }
              }
            }
          },
          "cursor": {
            "type": "string",
            "description": "Pagination cursor from a previous search response"
          },
          "limit": {
            "type": "integer",
            "description": "Maximum number of results to return (1-100)",
            "minimum": 1,
            "maximum": 100
          }
        },
        "example": {
          "cursor": "eyJsYXN0X3VwZGF0ZWRfYXQiOjE3NTMxMTg2NjQ4NzN9",
          "limit": 10,
          "query": {
            "filter": {
              "destination_location_ids": [
                "EXAMPLE_DEST_LOCATION_ID_456"
              ],
              "source_location_ids": [
                "EXAMPLE_SOURCE_LOCATION_ID_123"
              ],
              "statuses": [
                "STARTED",
                "PARTIALLY_RECEIVED"
              ]
            },
            "sort": {
              "field": "UPDATED_AT",
              "order": "DESC"
            }
          }
        }
      }
    }
  }
}
