http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Input schema for creating a new project",
        "properties": {
          "name": {
            "type": "string",
            "description": "Name of the project (required)"
          },
          "color": {
            "type": "string",
            "description": "Color of project - hex color code (e.g., '#F18181')",
            "example": "#F18181"
          },
          "sortOrder": {
            "type": "integer",
            "format": "int64",
            "description": "Sort order value of the project"
          },
          "viewMode": {
            "type": "string",
            "enum": [
              "list",
              "kanban",
              "timeline"
            ],
            "description": "View mode - how the project should be displayed (list, kanban, or timeline)"
          },
          "kind": {
            "type": "string",
            "enum": [
              "TASK",
              "NOTE"
            ],
            "description": "Project kind - type of items to store (TASK or NOTE)"
          }
        },
        "required": [
          "name"
        ]
      },
      "example": {
        "name": "project name",
        "color": "#F18181",
        "viewMode": "list",
        "kind": "task"
      }
    }
  }
}
