http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Input schema for updating an existing task",
        "properties": {
          "id": {
            "type": "string",
            "description": "Task id - must match the task being updated (required)"
          },
          "projectId": {
            "type": "string",
            "description": "Project id - must match the project containing the task (required)"
          },
          "title": {
            "type": "string",
            "description": "Task title"
          },
          "content": {
            "type": "string",
            "description": "Task content"
          },
          "desc": {
            "type": "string",
            "description": "Description of checklist"
          },
          "isAllDay": {
            "type": "boolean",
            "description": "All day flag"
          },
          "startDate": {
            "type": "string",
            "format": "date-time",
            "description": "Start date and time in 'yyyy-MM-dd'T'HH:mm:ssZ' format",
            "example": "2019-11-13T03:00:00+0000"
          },
          "dueDate": {
            "type": "string",
            "format": "date-time",
            "description": "Due date and time in 'yyyy-MM-dd'T'HH:mm:ssZ' format",
            "example": "2019-11-13T03:00:00+0000"
          },
          "timeZone": {
            "type": "string",
            "description": "The time zone in which the time is specified"
          },
          "reminders": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Lists of reminders specific to the task"
          },
          "repeatFlag": {
            "type": "string",
            "description": "Recurring rules of task"
          },
          "priority": {
            "type": "integer",
            "description": "The priority of task (None: 0, Low: 1, Medium: 3, High: 5)"
          },
          "sortOrder": {
            "type": "integer",
            "format": "int64",
            "description": "The order of task"
          },
          "items": {
            "type": "array",
            "items": {
              "type": "object",
              "description": "Input schema for creating or updating a checklist item",
              "properties": {
                "title": {
                  "type": "string",
                  "description": "Subtask title"
                },
                "startDate": {
                  "type": "string",
                  "format": "date-time",
                  "description": "Start date and time in 'yyyy-MM-dd'T'HH:mm:ssZ' format"
                },
                "isAllDay": {
                  "type": "boolean",
                  "description": "All day flag"
                },
                "sortOrder": {
                  "type": "integer",
                  "format": "int64",
                  "description": "The order of subtask"
                },
                "timeZone": {
                  "type": "string",
                  "description": "The time zone in which the Start time is specified"
                },
                "status": {
                  "type": "integer",
                  "format": "int32",
                  "description": "The completion status of subtask - Normal: 0, Completed: 1"
                },
                "completedTime": {
                  "type": "string",
                  "format": "date-time",
                  "description": "Completed time in 'yyyy-MM-dd'T'HH:mm:ssZ' format",
                  "example": "2019-11-13T03:00:00+0000"
                }
              }
            },
            "description": "The list of subtasks"
          }
        },
        "required": [
          "id",
          "projectId"
        ]
      },
      "example": {
        "id": "63b7bebb91c0a5474805fcd4",
        "projectId": "6226ff9877acee87727f6bca",
        "title": "Updated Task Title",
        "priority": 1
      }
    }
  }
}
