http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "idModel",
          "modelType",
          "name",
          "type",
          "pos"
        ],
        "properties": {
          "idModel": {
            "type": "string",
            "pattern": "^[0-9a-fA-F]{24}$",
            "example": "5abbe4b7ddc1b351ef961414"
          },
          "modelType": {
            "enum": [
              "board"
            ],
            "type": "string",
            "description": "The type of model that the Custom Field is being defined on. This should always be `board`."
          },
          "name": {
            "type": "string",
            "description": "The name of the Custom Field"
          },
          "type": {
            "enum": [
              "checkbox",
              "list",
              "number",
              "text",
              "date"
            ],
            "type": "string",
            "description": "The type of Custom Field to create."
          },
          "options": {
            "type": "string",
            "description": "If the type is `checkbox` "
          },
          "pos": {
            "oneOf": [
              {
                "type": "string",
                "enum": [
                  "top",
                  "bottom"
                ]
              },
              {
                "type": "number",
                "format": "float",
                "example": 1293.5
              }
            ]
          },
          "display_cardFront": {
            "type": "boolean",
            "description": "Whether this Custom Field should be shown on the front of Cards",
            "default": true
          }
        }
      }
    }
  }
}
