http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "additionalProperties": false,
        "properties": {
          "newProjectName": {
            "description": "The desired name for the project",
            "example": "a-project-name",
            "type": "string",
            "maxLength": 100
          },
          "paidFeatures": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
              "concurrentBuilds": {
                "type": "integer",
                "nullable": true
              },
              "passwordProtection": {
                "type": "boolean",
                "nullable": true
              },
              "previewDeploymentSuffix": {
                "type": "boolean",
                "nullable": true
              }
            }
          },
          "acceptedPolicies": {
            "type": "object",
            "additionalProperties": {
              "type": "object",
              "additionalProperties": {
                "type": "string",
                "format": "date-time"
              },
              "required": [
                "eula",
                "privacy"
              ],
              "properties": {
                "eula": {
                  "type": "string",
                  "format": "date-time"
                },
                "privacy": {
                  "type": "string",
                  "format": "date-time"
                }
              }
            }
          }
        }
      }
    }
  }
}
