http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "name": {
            "description": "The name of the log drain",
            "example": "My first log drain",
            "maxLength": 100,
            "pattern": "^[A-z0-9_ -]+$",
            "type": "string"
          },
          "projectIds": {
            "minItems": 1,
            "maxItems": 50,
            "type": "array",
            "items": {
              "pattern": "^[a-zA-z0-9_]+$",
              "type": "string"
            }
          },
          "secret": {
            "description": "A secret to sign log drain notification headers so a consumer can verify their authenticity",
            "example": "a1Xsfd325fXcs",
            "maxLength": 100,
            "pattern": "^[A-z0-9_ -]+$",
            "type": "string"
          },
          "deliveryFormat": {
            "description": "The delivery log format",
            "example": "json",
            "enum": [
              "json",
              "ndjson"
            ]
          },
          "url": {
            "description": "The url where you will receive logs. The protocol must be `https://` or `http://` when type is `json` and `ndjson`.",
            "example": "https://example.com/log-drain",
            "format": "uri",
            "pattern": "^https?://",
            "type": "string"
          },
          "sources": {
            "type": "array",
            "uniqueItems": true,
            "items": {
              "type": "string",
              "enum": [
                "static",
                "lambda",
                "build",
                "edge",
                "external",
                "firewall"
              ]
            },
            "minItems": 1
          },
          "headers": {
            "description": "Headers to be sent together with the request",
            "type": "object",
            "additionalProperties": {
              "type": "string"
            }
          },
          "environments": {
            "type": "array",
            "uniqueItems": true,
            "items": {
              "type": "string",
              "enum": [
                "preview",
                "production"
              ]
            },
            "minItems": 1
          }
        },
        "required": [
          "name",
          "url"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
