http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "oneOf": [
          {
            "type": "object",
            "required": [
              "key",
              "value",
              "type"
            ],
            "anyOf": [
              {
                "required": [
                  "target"
                ]
              },
              {
                "required": [
                  "customEnvironmentIds"
                ]
              }
            ],
            "properties": {
              "key": {
                "description": "The name of the environment variable",
                "type": "string",
                "example": "API_URL"
              },
              "value": {
                "description": "The value of the environment variable",
                "type": "string",
                "example": "https://api.vercel.com"
              },
              "type": {
                "description": "The type of environment variable",
                "type": "string",
                "enum": [
                  "system",
                  "secret",
                  "encrypted",
                  "plain",
                  "sensitive"
                ],
                "example": "plain"
              },
              "target": {
                "description": "The target environment of the environment variable",
                "type": "array",
                "items": {
                  "enum": [
                    "production",
                    "preview",
                    "development"
                  ]
                },
                "example": [
                  "preview"
                ]
              },
              "gitBranch": {
                "description": "If defined, the git branch of the environment variable (must have target=preview)",
                "type": "string",
                "maxLength": 250,
                "example": "feature-1",
                "nullable": true
              },
              "comment": {
                "type": "string",
                "description": "A comment to add context on what this environment variable is for",
                "example": "database connection string for production",
                "maxLength": 500
              },
              "customEnvironmentIds": {
                "type": "array",
                "description": "The custom environment IDs associated with the environment variable",
                "items": {
                  "type": "string",
                  "example": "env_1234567890"
                }
              }
            }
          },
          {
            "type": "array",
            "items": {
              "type": "object",
              "required": [
                "key",
                "value",
                "type"
              ],
              "anyOf": [
                {
                  "required": [
                    "target"
                  ]
                },
                {
                  "required": [
                    "customEnvironmentIds"
                  ]
                }
              ],
              "properties": {
                "key": {
                  "description": "The name of the environment variable",
                  "type": "string",
                  "example": "API_URL"
                },
                "value": {
                  "description": "The value of the environment variable",
                  "type": "string",
                  "example": "https://api.vercel.com"
                },
                "type": {
                  "description": "The type of environment variable",
                  "type": "string",
                  "enum": [
                    "system",
                    "secret",
                    "encrypted",
                    "plain",
                    "sensitive"
                  ],
                  "example": "plain"
                },
                "target": {
                  "description": "The target environment of the environment variable",
                  "type": "array",
                  "items": {
                    "enum": [
                      "production",
                      "preview",
                      "development"
                    ]
                  },
                  "example": [
                    "preview"
                  ]
                },
                "gitBranch": {
                  "description": "If defined, the git branch of the environment variable (must have target=preview)",
                  "type": "string",
                  "maxLength": 250,
                  "example": "feature-1",
                  "nullable": true
                },
                "comment": {
                  "type": "string",
                  "description": "A comment to add context on what this environment variable is for",
                  "example": "database connection string for production",
                  "maxLength": 500
                },
                "customEnvironmentIds": {
                  "type": "array",
                  "description": "The custom environment IDs associated with the environment variable",
                  "items": {
                    "type": "string",
                    "example": "env_1234567890"
                  }
                }
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
