http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "callbackUrl": {
            "type": "string",
            "description": "The URL to send a webhook to when the transfer is accepted."
          },
          "callbackSecret": {
            "type": "string",
            "description": "The secret to use to sign the webhook payload with HMAC-SHA256."
          }
        }
      }
    }
  }
}
