http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "email"
        ],
        "properties": {
          "email": {
            "type": "string",
            "format": "email",
            "description": "The email address of the user to invite",
            "example": "john@example.com"
          },
          "role": {
            "type": "string",
            "enum": [
              "OWNER",
              "MEMBER",
              "DEVELOPER",
              "SECURITY",
              "BILLING",
              "VIEWER",
              "VIEWER_FOR_PLUS",
              "CONTRIBUTOR"
            ],
            "description": "The role of the user to invite",
            "example": [
              "OWNER",
              "MEMBER",
              "DEVELOPER",
              "SECURITY",
              "BILLING",
              "VIEWER",
              "VIEWER_FOR_PLUS",
              "CONTRIBUTOR"
            ],
            "default": "MEMBER"
          },
          "projects": {
            "type": "array",
            "items": {
              "type": "object",
              "additionalProperties": false,
              "required": [
                "role",
                "projectId"
              ],
              "properties": {
                "projectId": {
                  "type": "string",
                  "maxLength": 64,
                  "example": "prj_ndlgr43fadlPyCtREAqxxdyFK",
                  "description": "The ID of the project."
                },
                "role": {
                  "type": "string",
                  "enum": [
                    "ADMIN",
                    "PROJECT_VIEWER",
                    "PROJECT_DEVELOPER"
                  ],
                  "example": "ADMIN",
                  "description": "Sets the project roles for the invited user"
                }
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
