http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "years",
          "expectedPrice"
        ],
        "properties": {
          "years": {
            "type": "number",
            "description": "The number of years to renew the domain for."
          },
          "expectedPrice": {
            "type": "number",
            "description": "The expected price for the domain. Use the [Get price data for a domain](https://vercel.com/docs/rest-api/reference/endpoints/domains-registrar/get-price-data-for-a-domain) endpoint to retrieve the price data for a domain.",
            "minimum": 0.01
          },
          "contactInformation": {
            "type": "object",
            "required": [
              "firstName",
              "lastName",
              "email",
              "phone",
              "address1",
              "city",
              "state",
              "zip",
              "country"
            ],
            "properties": {
              "firstName": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "lastName": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "email": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "minLength": 1
              },
              "phone": {
                "type": "string",
                "description": "A valid E.164 phone number",
                "title": "nonEmptyString",
                "minLength": 1,
                "pattern": "^(?=(?:\\D*\\d){7,15}$)\\+[1-9]\\d{0,2}\\.?\\d+$"
              },
              "address1": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "address2": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "city": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "state": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "zip": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "country": {
                "type": "string",
                "description": "A valid ISO 3166-1 alpha-2 country code",
                "title": "nonEmptyString",
                "minLength": 1,
                "pattern": "^[A-Z]{2}$"
              },
              "companyName": {
                "type": "string",
                "description": "a non empty string",
                "title": "nonEmptyString",
                "pattern": "^\\S[\\s\\S]*\\S$|^\\S$|^$",
                "minLength": 1
              },
              "fax": {
                "type": "string",
                "description": "A valid E.164 phone number",
                "title": "nonEmptyString",
                "minLength": 1,
                "pattern": "^(?=(?:\\D*\\d){7,15}$)\\+[1-9]\\d{0,2}\\.?\\d+$"
              }
            },
            "additionalProperties": false
          }
        },
        "additionalProperties": false
      }
    }
  },
  "required": true
}
