http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "firewallEnabled": {
            "type": "boolean"
          },
          "managedRules": {
            "type": "object",
            "additionalProperties": {
              "anyOf": []
            }
          },
          "crs": {
            "type": "object",
            "properties": {
              "sd": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Scanner Detection - Detect and prevent reconnaissance activities from network scanning tools."
              },
              "ma": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Multipart Attack - Block attempts to bypass security controls using multipart/form-data encoding."
              },
              "lfi": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Local File Inclusion Attack - Prevent unauthorized access to local files through web applications."
              },
              "rfi": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Remote File Inclusion Attack - Prohibit unauthorized upload or execution of remote files."
              },
              "rce": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Remote Execution Attack - Prevent unauthorized execution of remote scripts or commands."
              },
              "php": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "PHP Attack - Safeguard against vulnerability exploits in PHP-based applications."
              },
              "gen": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Generic Attack - Provide broad protection from various undefined or novel attack vectors."
              },
              "xss": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "XSS Attack - Prevent injection of malicious scripts into trusted webpages."
              },
              "sqli": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "SQL Injection Attack - Prohibit unauthorized use of SQL commands to manipulate databases."
              },
              "sf": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Session Fixation Attack - Prevent unauthorized takeover of user sessions by enforcing unique session IDs."
              },
              "java": {
                "type": "object",
                "properties": {
                  "active": {
                    "type": "boolean"
                  },
                  "action": {
                    "type": "string",
                    "enum": [
                      "deny",
                      "log"
                    ]
                  }
                },
                "required": [
                  "active",
                  "action"
                ],
                "additionalProperties": false,
                "description": "Java Attack - Mitigate risks of exploitation targeting Java-based applications or components."
              }
            },
            "additionalProperties": false,
            "description": "Custom Ruleset"
          },
          "rules": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "type": "string"
                },
                "name": {
                  "type": "string",
                  "maxLength": 160
                },
                "description": {
                  "type": "string",
                  "maxLength": 256
                },
                "active": {
                  "type": "boolean"
                },
                "conditionGroup": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "conditions": {
                        "type": "array",
                        "items": {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "host",
                                "path",
                                "method",
                                "header",
                                "query",
                                "cookie",
                                "target_path",
                                "route",
                                "raw_path",
                                "ip_address",
                                "region",
                                "protocol",
                                "scheme",
                                "environment",
                                "user_agent",
                                "geo_continent",
                                "geo_country",
                                "geo_country_region",
                                "geo_city",
                                "geo_as_number",
                                "ja4_digest",
                                "ja3_digest",
                                "rate_limit_api_id"
                              ],
                              "description": "[Parameter](https://vercel.com/docs/security/vercel-waf/rule-configuration#parameters) from the incoming traffic."
                            },
                            "op": {
                              "type": "string",
                              "enum": [
                                "re",
                                "eq",
                                "neq",
                                "ex",
                                "nex",
                                "inc",
                                "ninc",
                                "pre",
                                "suf",
                                "sub",
                                "gt",
                                "gte",
                                "lt",
                                "lte"
                              ]
                            },
                            "neg": {
                              "type": "boolean"
                            },
                            "key": {
                              "type": "string"
                            },
                            "value": {
                              "anyOf": [
                                {
                                  "type": "string"
                                },
                                {
                                  "type": "array",
                                  "items": {
                                    "type": "string"
                                  },
                                  "maxItems": 75
                                },
                                {
                                  "type": "number"
                                }
                              ]
                            }
                          },
                          "required": [
                            "type",
                            "op"
                          ],
                          "additionalProperties": false
                        },
                        "maxItems": 65
                      }
                    },
                    "required": [
                      "conditions"
                    ],
                    "additionalProperties": false
                  },
                  "maxItems": 25
                },
                "action": {
                  "type": "object",
                  "properties": {
                    "mitigate": {
                      "type": "object",
                      "properties": {
                        "action": {
                          "type": "string",
                          "enum": [
                            "log",
                            "challenge",
                            "deny",
                            "bypass",
                            "rate_limit",
                            "redirect"
                          ]
                        },
                        "rateLimit": {
                          "anyOf": [
                            {
                              "type": "object",
                              "properties": {
                                "algo": {
                                  "type": "string",
                                  "enum": [
                                    "fixed_window",
                                    "token_bucket"
                                  ]
                                },
                                "window": {
                                  "type": "number"
                                },
                                "limit": {
                                  "type": "number"
                                },
                                "keys": {
                                  "type": "array",
                                  "items": {
                                    "type": "string"
                                  }
                                },
                                "action": {
                                  "anyOf": [
                                    {
                                      "type": "string",
                                      "enum": [
                                        "log",
                                        "challenge",
                                        "deny",
                                        "rate_limit"
                                      ]
                                    },
                                    {}
                                  ],
                                  "nullable": true
                                }
                              },
                              "required": [
                                "algo",
                                "window",
                                "limit",
                                "keys"
                              ],
                              "additionalProperties": false
                            },
                            {}
                          ],
                          "nullable": true
                        },
                        "redirect": {
                          "anyOf": [
                            {
                              "type": "object",
                              "properties": {
                                "location": {
                                  "type": "string"
                                },
                                "permanent": {
                                  "type": "boolean"
                                }
                              },
                              "required": [
                                "location",
                                "permanent"
                              ],
                              "additionalProperties": false
                            },
                            {}
                          ],
                          "nullable": true
                        },
                        "actionDuration": {
                          "type": "string",
                          "nullable": true
                        },
                        "bypassSystem": {
                          "type": "boolean",
                          "nullable": true
                        }
                      },
                      "required": [
                        "action"
                      ],
                      "additionalProperties": false
                    }
                  },
                  "additionalProperties": false
                }
              },
              "required": [
                "name",
                "active",
                "conditionGroup",
                "action"
              ],
              "additionalProperties": false
            }
          },
          "ips": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "type": "string"
                },
                "hostname": {
                  "type": "string"
                },
                "ip": {
                  "type": "string"
                },
                "notes": {
                  "type": "string"
                },
                "action": {
                  "type": "string",
                  "enum": [
                    "deny",
                    "challenge",
                    "log",
                    "bypass"
                  ]
                }
              },
              "required": [
                "hostname",
                "ip",
                "action"
              ],
              "additionalProperties": false
            }
          },
          "botIdEnabled": {
            "type": "boolean"
          }
        },
        "required": [
          "firewallEnabled"
        ],
        "additionalProperties": false
      }
    }
  },
  "required": true
}
