http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "name": {
            "description": "The name of the check being created",
            "maxLength": 100,
            "example": "Performance Check",
            "type": "string"
          },
          "path": {
            "description": "Path of the page that is being checked",
            "type": "string",
            "maxLength": 255,
            "example": "/"
          },
          "status": {
            "description": "The current status of the check",
            "enum": [
              "running",
              "completed"
            ]
          },
          "conclusion": {
            "description": "The result of the check being run",
            "enum": [
              "canceled",
              "failed",
              "neutral",
              "succeeded",
              "skipped"
            ]
          },
          "detailsUrl": {
            "description": "A URL a user may visit to see more information about the check",
            "type": "string",
            "example": "https://example.com/check/run/1234abc"
          },
          "output": {
            "description": "The results of the check Run",
            "type": "object",
            "properties": {
              "metrics": {
                "type": "object",
                "description": "Metrics about the page",
                "required": [
                  "FCP",
                  "LCP",
                  "CLS",
                  "TBT"
                ],
                "additionalProperties": false,
                "properties": {
                  "FCP": {
                    "type": "object",
                    "required": [
                      "value",
                      "source"
                    ],
                    "properties": {
                      "value": {
                        "type": "number",
                        "example": 1200,
                        "description": "First Contentful Paint value",
                        "nullable": true
                      },
                      "previousValue": {
                        "type": "number",
                        "example": 900,
                        "description": "Previous First Contentful Paint value to display a delta"
                      },
                      "source": {
                        "type": "string",
                        "enum": [
                          "web-vitals"
                        ]
                      }
                    }
                  },
                  "LCP": {
                    "type": "object",
                    "required": [
                      "value",
                      "source"
                    ],
                    "properties": {
                      "value": {
                        "type": "number",
                        "example": 1200,
                        "description": "Largest Contentful Paint value",
                        "nullable": true
                      },
                      "previousValue": {
                        "type": "number",
                        "example": 1000,
                        "description": "Previous Largest Contentful Paint value to display a delta"
                      },
                      "source": {
                        "type": "string",
                        "enum": [
                          "web-vitals"
                        ]
                      }
                    }
                  },
                  "CLS": {
                    "type": "object",
                    "required": [
                      "value",
                      "source"
                    ],
                    "properties": {
                      "value": {
                        "type": "number",
                        "example": 4,
                        "description": "Cumulative Layout Shift value",
                        "nullable": true
                      },
                      "previousValue": {
                        "type": "number",
                        "example": 2,
                        "description": "Previous Cumulative Layout Shift value to display a delta"
                      },
                      "source": {
                        "type": "string",
                        "enum": [
                          "web-vitals"
                        ]
                      }
                    }
                  },
                  "TBT": {
                    "type": "object",
                    "required": [
                      "value",
                      "source"
                    ],
                    "properties": {
                      "value": {
                        "type": "number",
                        "example": 3000,
                        "description": "Total Blocking Time value",
                        "nullable": true
                      },
                      "previousValue": {
                        "type": "number",
                        "example": 3500,
                        "description": "Previous Total Blocking Time value to display a delta"
                      },
                      "source": {
                        "enum": [
                          "web-vitals"
                        ]
                      }
                    }
                  },
                  "virtualExperienceScore": {
                    "type": "object",
                    "required": [
                      "value",
                      "source"
                    ],
                    "properties": {
                      "value": {
                        "type": "integer",
                        "maximum": 100,
                        "minimum": 0,
                        "example": 30,
                        "description": "The calculated Virtual Experience Score value, between 0 and 100",
                        "nullable": true
                      },
                      "previousValue": {
                        "type": "integer",
                        "maximum": 100,
                        "minimum": 0,
                        "example": 35,
                        "description": "A previous Virtual Experience Score value to display a delta, between 0 and 100"
                      },
                      "source": {
                        "enum": [
                          "web-vitals"
                        ]
                      }
                    }
                  }
                }
              }
            }
          },
          "externalId": {
            "description": "An identifier that can be used as an external reference",
            "type": "string",
            "example": "1234abc"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
