http_endpoint.request_body_spec:
{
  "description": "Details of the restore request, including collections to include or exclude and node mapping if necessary.",
  "content": {
    "application/yaml": {
      "schema": {
        "type": "object",
        "properties": {
          "config": {
            "type": "object",
            "properties": {
              "Endpoint": {
                "type": "string",
                "description": "name of the endpoint, e.g. s3.amazonaws.com"
              },
              "Bucket": {
                "type": "string",
                "description": "Name of the bucket, container, volume, etc"
              },
              "Path": {
                "type": "string",
                "description": "Path within the bucket"
              },
              "CPUPercentage": {
                "maximum": 80,
                "minimum": 1,
                "type": "integer",
                "description": "Desired CPU core utilization ranging from 1%-80%",
                "nullable": false
              },
              "rolesOptions": {
                "type": "string",
                "description": "How roles should be restored",
                "default": "noRestore",
                "enum": [
                  "noRestore",
                  "all"
                ]
              },
              "usersOptions": {
                "type": "string",
                "description": "How users should be restored",
                "default": "noRestore",
                "enum": [
                  "noRestore",
                  "all"
                ]
              }
            },
            "description": "Backup custom configuration"
          },
          "include": {
            "type": "array",
            "description": "List of collections (classes) to include in the backup restoration process.",
            "items": {
              "type": "string"
            }
          },
          "exclude": {
            "type": "array",
            "description": "List of collections (classes) to exclude from the backup restoration process.",
            "items": {
              "type": "string"
            }
          },
          "node_mapping": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "Allows overriding the node names stored in the backup with different ones. Useful when restoring backups to a different environment."
          },
          "overwriteAlias": {
            "type": "boolean",
            "description": "Allows ovewriting the collection alias if there is a conflict"
          }
        },
        "description": "Request body for restoring a backup for a set of collections (classes)."
      }
    },
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "config": {
            "type": "object",
            "properties": {
              "Endpoint": {
                "type": "string",
                "description": "name of the endpoint, e.g. s3.amazonaws.com"
              },
              "Bucket": {
                "type": "string",
                "description": "Name of the bucket, container, volume, etc"
              },
              "Path": {
                "type": "string",
                "description": "Path within the bucket"
              },
              "CPUPercentage": {
                "maximum": 80,
                "minimum": 1,
                "type": "integer",
                "description": "Desired CPU core utilization ranging from 1%-80%",
                "nullable": false
              },
              "rolesOptions": {
                "type": "string",
                "description": "How roles should be restored",
                "default": "noRestore",
                "enum": [
                  "noRestore",
                  "all"
                ]
              },
              "usersOptions": {
                "type": "string",
                "description": "How users should be restored",
                "default": "noRestore",
                "enum": [
                  "noRestore",
                  "all"
                ]
              }
            },
            "description": "Backup custom configuration"
          },
          "include": {
            "type": "array",
            "description": "List of collections (classes) to include in the backup restoration process.",
            "items": {
              "type": "string"
            }
          },
          "exclude": {
            "type": "array",
            "description": "List of collections (classes) to exclude from the backup restoration process.",
            "items": {
              "type": "string"
            }
          },
          "node_mapping": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "Allows overriding the node names stored in the backup with different ones. Useful when restoring backups to a different environment."
          },
          "overwriteAlias": {
            "type": "boolean",
            "description": "Allows ovewriting the collection alias if there is a conflict"
          }
        },
        "description": "Request body for restoring a backup for a set of collections (classes)."
      }
    }
  },
  "required": true
}
