http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "invoice_id",
          "amount_applied"
        ],
        "type": "object",
        "properties": {
          "invoices": {
            "description": "List of invoices for which the credit note has been raised. This contains <code>invoice_id</code> and <code>amount</code>.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "invoice_id": {
                  "description": "Invoice ID of the required invoice.",
                  "type": "string",
                  "example": "90300000079426"
                },
                "amount_applied": {
                  "description": "The total amount applied form the credit note on an invoice.",
                  "type": "number",
                  "format": "double",
                  "example": 41.82
                }
              }
            }
          },
          "invoice_id": {
            "description": "Invoice ID of the required invoice.",
            "type": "string",
            "example": "90300000079426"
          },
          "amount_applied": {
            "description": "The total amount applied form the credit note on an invoice.",
