http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "customer_id",
          "line_items"
        ],
        "type": "object",
        "properties": {
          "customer_id": {
            "description": "The ID of the customer (contact) for whom the estimate is created. This must be a valid contact ID from your organization; obtain it using the Contacts API (GET /contacts) or the Create Contact response.",
            "type": "string",
            "example": 982000000567001
          },
          "currency_id": {
            "description": "The ID of the currency in which the estimate will be created. Use this to specify the customer\u2019s preferred currency for transactions. Fetch valid currency IDs using the Currency API (GET /currencies).",
            "type": "string",
            "example": 982000000000190
          },
          "contact_persons_associated": {
            "description": "Contact Persons associated with the estimate.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "contact_person_id": {
                  "description": "Unique ID of the Contact Person.",
                  "type": "integer",
                  "format": "int64",
                  "example": 982000000567003
                },
                "communication_preference": {
                  "description": "Preferred modes of communication for the contact person at transaction level.",
                  "type": "object",
                  "properties": {
                    "is_email_enabled": {
                      "description": "Used to check if Email communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true
                    },
                    "is_whatsapp_enabled": {
                      "description": "Used to check if WhatsApp communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true,
                      "x-node_available_in": [
                        "WhatsApp integration"
                      ]
                    }
                  }
                }
              }
            }
          },
          "template_id": {
            "description": "The ID of the template used for generating the estimate PDF. Use this to apply custom branding or formats. Fetch template IDs using the Templates API (GET /estimates/templates).",
            "type": "string",
            "example": 982000000000143
          },
          "place_of_supply": {
            "description": "Place where the goods/services are supplied to. (If not given, <code>place of contact</code> given for the contact will be taken)<br/>Supported codes for UAE emirates are : </br>Abu Dhabi - <code>AB</code>,</br>Ajman - <code>AJ</code>,</br>Dubai - <code>DU</code>,</br>Fujairah - <code>FU</code>,</br>Ras al-Khaimah - <code>RA</code>,</br>Sharjah - <code>SH</code>,</br>Umm al-Quwain - <code>UM</code><br/>Supported codes for the GCC countries are : </br>United Arab Emirates - <code>AE</code>,</br>Saudi Arabia - <code>SA</code>,</br>Bahrain - <code>BH</code>,</br>Kuwait - <code>KW</code>,</br>Oman - <code>OM</code>,</br>Qatar - <code>QA</code>.",
            "type": "string",
            "example": "TN",
            "x-node_available_in": [
              "in",
              "gcc"
            ],
            "x-node_unavailable_in": []
          },
          "gst_treatment": {
            "description": "Choose whether the contact is GST registered/unregistered/consumer/overseas. Allowed values are <code> business_gst </code> , <code> business_none </code> , <code> overseas </code> , <code> consumer </code>.",
            "type": "string",
            "example": "business_gst",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "gst_no": {
            "description": "15 digit GST identification number of the customer.",
            "type": "string",
            "example": "22AAAAA0000A1Z5",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "estimate_number": {
            "description": "Search estimates by estimate number.Variants<code>estimate_number_startswith</code> and <code>estimate_number_contains</code>",
            "type": "string",
            "example": "EST-00002"
          },
          "reference_number": {
            "description": "Optional external identifier used to associate the estimate with other systems or workflows. Helps improve traceability and integration during estimate creation.",
            "type": "string",
            "example": "QRT-12346"
          },
          "date": {
            "description": "Search estimates by estimate date.Variants <code>date_start</code>, <code>date_end</code>, <code>date_before</code> and <code>date_after</code>",
            "type": "string",
            "example": "2013-11-18"
          },
          "expiry_date": {
            "description": "The date of expiration of the estimates",
            "type": "string",
            "example": "2013-11-30"
          },
          "exchange_rate": {
            "description": "Exchange rate of the currency.",
            "type": "number",
            "format": "double",
            "example": 1
          },
          "discount": {
            "description": "Discount applied to the estimate. It can be either in % or in amount. e.g. 12.5% or 190.",
            "type": "number",
            "format": "double",
            "example": 0
          },
          "is_discount_before_tax": {
            "description": "Used to specify how the discount has to applied. Either before or after the calculation of tax.",
            "type": "boolean",
            "example": true
          },
          "discount_type": {
            "description": "How the discount is specified. Allowed values are entity_level or item_level.Allowed Values: <code>entity_level</code> and <code>item_level</code>",
            "type": "string",
            "example": "item_level"
          },
          "is_inclusive_tax": {
            "description": "Used to specify whether the line item rates are inclusive or exclusive of tax.",
            "type": "boolean",
            "example": false,
            "x-node_available_in": [],
            "x-node_unavailable_in": [
              "us",
              "ca"
            ]
          },
          "custom_body": {
            "type": "string",
            "example": " "
          },
          "custom_subject": {
            "type": "string",
            "example": " "
          },
          "salesperson_name": {
            "description": "Name of the sales person.",
            "type": "string",
            "example": "Will smith"
          },
          "custom_fields": {
            "description": "Custom fields for an estimate.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "index": {
                  "type": "integer",
                  "example": 1
                },
                "value": {
                  "type": "string",
                  "example": "15 Dec 2013"
                }
              }
            }
          },
          "line_items": {
            "description": "Line items of an estimate.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "item_id": {
                  "description": "Unique identifier of the item (product or service) being quoted in this line item. Use the `item_id` returned by the Items API for the same organization. This links the line item to a specific product or service in your catalog.",
                  "type": "string",
                  "example": " "
                },
                "line_item_id": {
                  "description": "Unique identifier of the line item within the estimate. Required when updating or referencing a specific line item. If omitted during creation, a new line item will be added.",
                  "type": "string",
                  "example": 982000000567021
                },
                "name": {
                  "description": "Name of the product or service for this line item. This is displayed on the estimate document and helps the customer identify what is being quoted.",
                  "type": "string",
                  "example": "Hard Drive"
                },
                "description": {
                  "description": "Detailed description of the product or service in this line item. Use this field to provide specifications, features, or any other relevant information that helps the customer understand about the line item.",
                  "type": "string",
                  "example": "500GB, USB 2.0 interface 1400 rpm, protective hard case."
                },
                "product_type": {
                  "description": "Type of product or service for this line item. Allowed values: <code>goods</code> or <code>services</code>. For South Africa Edition, additional values include <code>capital_service</code> and <code>capital_goods</code>. This classification affects tax calculations, reporting, and compliance requirements based on your organization's location and tax regulations.",
                  "type": "string",
                  "example": "goods"
                },
                "hsn_or_sac": {
                  "description": "Add HSN/SAC code for your goods/services",
                  "type": "string",
                  "example": 80540,
                  "x-node_available_in": [
                    "in",
                    "ke",
                    "za"
                  ],
                  "x-node_unavailable_in": []
                },
                "sat_item_key_code": {
                  "description": "Add SAT Item Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": 71121206,
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "unitkey_code": {
                  "description": "Add SAT Unit Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": "E48",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "item_order": {
                  "description": "Sequential order number for this line item on the estimate. Determines the display order of line items in the estimate document \u2014 lower values appear first. Use this field to organize line items in a specific sequence.",
                  "type": "integer",
                  "example": 1
                },
                "bcy_rate": {
                  "description": "Exchange rate between the base currency and the transaction currency for this line item. Used for currency conversion and reporting.",
                  "type": "number",
                  "format": "float",
                  "example": 120
                },
                "rate": {
                  "description": "Unit price or rate for the product or service in this line item. This value is multiplied by the quantity to determine the line item total before taxes and discounts.",
                  "type": "number",
                  "format": "double",
                  "example": 120
                },
                "quantity": {
                  "description": "Number of units of the product or service being quoted in this line item. This value is multiplied by the rate to calculate the line item total.",
                  "type": "number",
                  "format": "double",
                  "example": 1
                },
                "unit": {
                  "description": "Unit of measurement for the product or service in this line item (e.g., kgs, Nos., hours). Helps clarify the quantity being quoted.",
                  "type": "string",
                  "example": " "
                },
                "discount_amount": {
                  "description": "Discount amount applied to this line item. This value reduces the line item total before tax calculation.",
                  "type": "number",
                  "format": "float",
                  "example": 0
                },
                "discount": {
                  "description": "Discount applied to the estimate. It can be either in % or in amount. e.g. 12.5% or 190.",
                  "type": "number",
                  "format": "double",
                  "example": 0
                },
                "tax_id": {
                  "description": "Unique identifier of the tax to be applied to this line item. Use the `tax_id` returned by the Taxes API for the same organization. This determines which tax rate and rules are applied to the line item, affecting the final tax calculation and compliance reporting.",
                  "type": "string",
                  "example": 982000000557028
                },
                "tds_tax_id": {
                  "description": "ID of the TDS tax.",
                  "type": "string",
                  "example": "982000000557012",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_name": {
                  "description": "The name of the tax",
                  "type": "string",
                  "example": "VAT"
                },
                "tax_type": {
                  "description": "The type of the tax",
                  "type": "string",
                  "example": "tax"
                },
                "tax_percentage": {
                  "description": "The  percentage of tax levied",
                  "type": "number",
                  "format": "float",
                  "example": 12.5
                },
                "tax_treatment_code": {
                  "description": "Specify reason for using out of scope.</br>Supported values for <b>UAE</b> are <code>uae_same_tax_group</code>, <code>uae_reimbursed_expense</code> and <code>uae_others</code>.</br> Supported values for <b>Bahrain</b> are <code>bahrain_same_tax_group</code>, <code>bahrain_transfer_of_concern</code>, <code>bahrain_disbursement</code>, <code>bahrain_head_to_branch_transaction</code>, <code>bahrain_warranty_repair_services</code> and <code>bahrain_others</code>.</br>Supported values for <b>Saudi Arabia</b> are <code>ksa_pvt_health</code>, <code>ksa_pvt_edu</code>, <code>ksa_reimbursed_expense</code> and <code>ksa_house_sales</code>.",
                  "type": "string",
                  "example": "uae_others",
                  "x-node_available_in": [
                    "gcc"
                  ],
                  "x-node_unavailable_in": []
                },
                "item_total": {
                  "description": "Total amount for this line item after applying quantity, rate, discounts, and taxes. This value contributes to the overall estimate total.",
                  "type": "number",
                  "format": "float",
                  "example": 120
                },
                "location_id": {
                  "description": "Unique identifier of the business location associated with the estimate. Use the `location_id` returned by the Locations API for the same organization. This links the estimate to a specific business location, which may affect tax calculations, reporting, and business operations. Required if your organization has multiple locations configured.",
                  "type": "string",
                  "example": "460000000038080"
                }
              }
            }
          },
          "location_id": {
            "description": "Unique identifier of the business location associated with the estimate. Use the `location_id` returned by the Locations API for the same organization. This links the estimate to a specific business location, which may affect tax calculations, reporting, and business operations. Required if your organization has multiple locations configured.",
            "type": "string",
            "example": "460000000038080"
          },
          "notes": {
            "description": "Additional information or comments to be displayed on the estimate document. This field is typically used for thank you messages, special instructions, or any other information you want to communicate to the customer. The notes appear on the estimate PDF and are visible to customers.",
            "type": "string",
            "example": "Looking forward for your business."
          },
          "terms": {
            "description": "Defines the terms and conditions to be included in the estimate document. This field can be used to specify payment terms, delivery schedules, return policies, or other transactional details. The terms appear on the estimate PDF and helps communicate key conditions related to the estimate.",
            "type": "string",
            "example": "Terms & Conditions apply"
          },
          "shipping_charge": {
            "description": "Additional shipping or delivery charges applied to the estimate. Must be specified as a fixed amount (e.g., \"25.00\"). This amount is added to the estimate total and is subject to tax calculation.",
            "type": "string",
            "example": 0
          },
          "adjustment": {
            "description": "Additional charge or credit amount applied to the estimate total. This can be used for rounding adjustments, handling fees, or any other one-time charges/credits. Positive values add to the total, negative values reduce the total. This amount is applied after tax calculation and affects the final estimate total.",
            "type": "number",
            "format": "double",
            "example": 0
          },
          "adjustment_description": {
            "description": "Text description explaining the reason for the adjustment amount. This description appears on the estimate document to explain why the adjustment was applied. Common examples include \"Rounding adjustment\", \"Handling fee\", \"Early payment discount\", or \"Late payment fee\". This helps customers understand the purpose of the adjustment.",
            "type": "string",
            "example": " "
          },
          "tax_id": {
            "description": "Unique identifier of the tax to be applied to this line item. Use the `tax_id` returned by the Taxes API for the same organization. This determines which tax rate and rules are applied to the line item, affecting the final tax calculation and compliance reporting.",
            "type": "string",
            "example": 982000000557028
          },
          "tax_exemption_id": {
            "description": "ID of the tax exemption.",
            "type": "string",
            "example": 11149000000061054,
            "x-node_available_in": [
              "in",
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "tax_authority_id": {
            "description": "ID of the tax authority. Tax authority depends on the location of the customer. For example, if the customer is located in NY, then the tax authority is NY tax authority.",
            "type": "string",
            "example": 11149000000061052,
            "x-node_available_in": [
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_use_code": {
            "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule. Select from Avalara [standard codes][1] or enter a custom code.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_exempt_no": {
            "description": "Exemption certificate number of the customer.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "vat_treatment": {
            "description": "(Optional) VAT treatment for the estimates. VAT treatment denotes the location of the customer, if the customer resides in UK then the VAT treatment is <code>uk</code>. If the customer is in an EU country & VAT registered, you are resides in Northen Ireland and selling Goods then his VAT treatment is <code>eu_vat_registered</code>, if he resides outside of the UK then his VAT treatment is <code>overseas</code> (For Pre Brexit, this can be split as <code>eu_vat_registered</code>, <code>eu_vat_not_registered</code> and <code>non_eu</code>).",
            "type": "string",
            "x-node_available_in": [
              "uk"
            ],
            "x-node_unavailable_in": []
          },
          "tax_treatment": {
            "description": "VAT treatment for the Estimate. Allowed Values: <code>vat_registered</code>,<code>vat_not_registered</code>,<code>gcc_vat_not_registered</code>,<code>gcc_vat_registered</code>,<code>non_gcc</code>.</br><code>dz_vat_registered</code> and <code>dz_vat_not_registered</code> supported only for <b>UAE.</b></br><code>home_country_mexico</code>,<code>border_region_mexico</code>,<code>non_mexico</code> supported only for <b>MX</b>. <br/><b>For Kenya Edition:</b> <code>vat_registered</code> ,<code>vat_not_registered</code> ,<code>non_kenya</code>(A business that is located outside Kenya).<br/><b>For SouthAfrica Edition:</b> <code>vat_registered</code>, <code>vat_not_registered</code>, <code>overseas</code>(A business that is located outside SouthAfrica).",
            "type": "string",
            "example": "vat_registered",
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "is_reverse_charge_applied": {
            "description": "(Required if customer tax treatment is <code>vat_registered</code>)<br/>Used to specify whether the transaction is applicable for Domestic Reverse Charge (DRC) or not.",
            "type": "boolean",
            "example": true,
            "x-node_available_in": [
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "item_id": {
            "description": "Unique identifier of the item (product or service) being quoted in this line item. Use the `item_id` returned by the Items API for the same organization. This links the line item to a specific product or service in your catalog.",
            "type": "string",
            "example": " "
          },
          "line_item_id": {
            "description": "Unique identifier of the line item within the estimate. Required when updating or referencing a specific line item. If omitted during creation, a new line item will be added.",
            "type": "string",
            "example": 982000000567021
          },
          "name": {
            "description": "Name of the product or service for this line item. This is displayed on the estimate document and helps the customer identify what is being quoted.",
            "type": "string",
            "example": "Hard Drive"
          },
          "description": {
            "description": "Detailed description of the product or service in this line item. Use this field to provide specifications, features, or any other relevant information that helps the customer understand about the line item.",
            "type": "string",
            "example": "500GB, USB 2.0 interface 1400 rpm, protective hard case."
          },
          "rate": {
            "description": "Unit price or rate for the product or service in this line item. This value is multiplied by the quantity to determine the line item total before taxes and discounts.",
            "type": "number",
            "format": "double",
            "example": 120
          },
          "unit": {
            "description": "Unit of measurement for the product or service in this line item (e.g., kgs, Nos., hours). Helps clarify the quantity being quoted.",
            "type": "string",
            "example": " "
          },
          "quantity": {
            "description": "Number of units of the product or service being quoted in this line item. This value is multiplied by the rate to calculate the line item total.",
            "type": "number",
            "format": "double",
            "example": 1
          },
          "project_id": {
            "description": "Unique identifier of the project associated with this estimate. Use the <code>project_id</code> returned by the Projects API for the same organization. Linking an estimate to a project helps with project-based billing, tracking, and reporting.",
            "type": "string",
            "example": 90300000087378
          },
          "accept_retainer": {
            "description": "Boolean flag indicating whether a retainer invoice should be automatically created if the customer accepts this estimate. When set to <code>true</code>, a retainer invoice is generated upon acceptance, streamlining the process for upfront payments or deposits. Allowed values: <code>true</code> and <code>false</code>.",
            "type": "boolean",
            "example": true
          },
          "retainer_percentage": {
            "description": "Percentage of the estimate amount to be collected as a retainer (upfront payment) when the estimate is accepted. Specify a value between 0 and 100. This determines the portion of the total estimate that will be invoiced as a retainer.",
            "type": "integer",
            "example": 10
          }
        }
      }
    }
  }
}
