http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "customer_id",
          "payment_mode",
          "amount",
          "invoices",
          "date",
          "invoice_id",
          "amount_applied"
        ],
        "type": "object",
        "properties": {
          "customer_id": {
            "description": "Customer ID of the customer involved in the payment.",
            "type": "string",
            "example": "903000000000099"
          },
          "payment_mode": {
            "description": "Mode through which payment is made. This can be <code>check</code>, <code>cash</code>, <code>creditcard</code>, <code>banktransfer</code>, <code>bankremittance</code>, <code>autotransaction</code> or <code>others</code>. Max-length [100]",
            "type": "string",
            "example": "cash"
          },
          "amount": {
            "description": "Amount paid in the respective payment.",
            "type": "number",
            "format": "double",
            "example": 450
          },
          "date": {
            "description": "Date on which payment is made. Format [yyyy-mm-dd]",
            "type": "string",
            "example": "2016-06-05"
          },
          "reference_number": {
            "description": "Reference number generated for the payment. A string of your choice can also be used as the reference number. Max-length [100]",
            "type": "string",
            "example": "INV-384"
          },
          "description": {
            "description": "Description about the payment.",
            "type": "string",
            "example": "Payment has been added to INV-384"
          },
          "invoices": {
            "description": "List of invoices associated with the payment. Each invoice object contains <code>invoice_id</code>, <code>invoice_number</code>, <code>date</code>, <code>invoice_amount</code>, <code>amount_applied</code> and <code>balance_amount</code>.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "invoice_id": {
                  "description": "Invoice ID of the required invoice.",
                  "type": "string",
                  "example": "90300000079426"
                },
                "amount_applied": {
                  "description": "Amount paid for the invoice.",
                  "type": "number",
                  "format": "double",
                  "example": 450
                }
              }
            }
          },
          "exchange_rate": {
            "description": "Exchange rate for the currency used in the invoices and customer's currency. The payment amount would be the multiplicative product of the original amount and the exchange rate.",
            "type": "number",
            "format": "double",
            "example": 1,
            "default": 1
          },
          "payment_form": {
            "description": "Mode of Vendor Payment",
            "type": "string",
            "example": "cash",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "bank_charges": {
            "description": "Denotes any additional bank charges.",
            "type": "number",
            "format": "double",
            "example": 10
          },
          "custom_fields": {
            "description": "Additional fields for the payments.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "label": {
                  "description": "Label of the custom field.",
                  "type": "string",
                  "example": "label"
                },
                "value": {
                  "description": "Value of the custom field.",
                  "type": "string",
                  "example": 129890
                }
              }
            }
          },
          "location_id": {
            "description": "Location ID",
            "type": "string",
            "example": "460000000038080"
          },
          "invoice_id": {
            "description": "Invoice ID of the required invoice.",
            "type": "string",
            "example": "90300000079426"
          },
          "amount_applied": {
            "description": "Amount paid for the invoice.",
            "type": "number",
            "format": "double",
            "example": 450
          },
          "tax_amount_withheld": {
            "description": "Amount withheld for tax.",
            "type": "number",
            "format": "double",
            "example": 0
          },
          "account_id": {
            "description": "ID of the cash/bank account the payment has to be deposited.",
            "type": "string",
            "example": " "
          },
          "contact_persons": {
            "description": "IDs of the contact personsthe thank you mail has to be triggered.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "982000000870911",
              "982000000870915"
            ]
          },
          "retainerinvoice_id": {
            "description": "ID of the retainer invoice associated with the payment",
            "type": "string",
            "example": "982000000567114"
          }
        }
      }
    }
  }
}
