http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "to_mail_ids",
          "subject",
          "body"
        ],
        "type": "object",
        "properties": {
          "send_from_org_email_id": {
            "description": "Boolean to trigger the email from the organization's email address",
            "type": "boolean",
            "example": true
          },
          "to_mail_ids": {
            "description": "Array of email address of the recipients.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "willsmith@bowmanfurniture.com"
            ]
          },
          "cc_mail_ids": {
            "description": "Array of email address of the recipients to be cced.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "peterparker@bowmanfurniture.com"
            ]
          },
          "subject": {
            "description": "Subject of an email has to be sent. Max-length [1000]",
            "type": "string",
            "example": "Statement of transactions with Zillium Inc"
          },
          "body": {
            "description": "Body of an email has to be sent. Max-length [5000]",
            "type": "string",
            "example": "Dear Customer,     <br/>We have attached with this email a list of all your transactions with us for the period 01 Sep 2013 to 30 Sep 2013. You can write to us or call us if you need any assistance or clarifications.     <br/>Thanks for your business.<br/>Regards<br/>Zillium Inc"
          }
        }
      }
    }
  }
}
