http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "to_mail_ids",
          "subject",
          "body"
        ],
        "type": "object",
        "properties": {
          "from_address_id": {
            "description": "From Address of the Email Address",
            "type": "string",
            "example": "johnRoberts@safInstrument.com"
          },
          "send_from_org_email_id": {
            "description": "Boolean to trigger the email from the organization's email address.",
            "type": "boolean",
            "example": true
          },
          "to_mail_ids": {
            "description": "Array of email address of the recipients.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "john@safInstruments.com"
            ]
          },
          "cc_mail_ids": {
            "description": "Array of email address of the recipients to be CC ed.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "smith@safInstruments.com"
            ]
          },
          "bcc_mail_ids": {
            "description": "Array of email address of the recipients to be BCC ed.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "mark@safInstruments.com"
            ]
          },
          "subject": {
            "description": "Subject of the mail.",
            "type": "string",
            "example": "Sales Order from Zillium Inc (Sales Order #: SO-00001)"
          },
          "documents": {
            "description": "Documents of the Sales Order",
            "type": "string",
            "format": "binary"
          },
          "invoice_id": {
            "description": "ID of the invoice",
            "type": "string",
            "example": 460000000028192
          },
          "body": {
            "description": "Body of the mail.",
            "type": "string",
            "example": "<br>Dear SAF Instruments Inc,&nbsp;<br><br>Thanks for your interest in our services. Please find our sales order attached with this mail.<br><br> An overview of the sales order is available below for your reference: &nbsp;<br><br> ----------------------------------------------------------------------------------------<br> <h2>Sales Order&nbsp;# :&nbsp;SO-00001</h2><br> ----------------------------------------------------------------------------------------<br> <b>&nbsp;Order Date &nbsp; &nbsp;&nbsp;&nbsp;: &nbsp;28 Jul 2014</b><br><b>&nbsp;Amount &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;&nbsp;$12,400.00</b><br>----------------------------------------------------------------------------------------<br><br><span>Assuring you of our best services at all times.</span><br><br><br>Regards,<br><br>John<br>Zillium Inc<br><br><br>"
          }
        }
      }
    }
  }
}
