http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "invoice_id",
          "line_item_id",
          "reason",
          "expense_account_id"
        ],
        "type": "object",
        "properties": {
          "expense_account_id": {
            "description": "Enter the account id to track the expenses associated with the asset\u2019s depreciation.The available account types are <b>Expense</b>  and <b>Other Expense</b>",
            "type": "string",
            "example": "3640355000000000421"
          },
          "invoice_id": {
            "type": "string",
            "description": "Enter the invoice id associated with selling the fixed asset.",
            "example": "3640355000000319008"
          },
          "line_item_id": {
            "type": "string",
            "description": "Enter the line item id that is associated with the fixed asset account.",
            "example": "3640355000000319008"
          },
          "reason": {
            "type": "string",
            "description": "Enter the reason for selling the fixed asset",
            "example": "Asset is no longer required"
          }
        }
      }
    }
  }
}
