http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "to_mail_ids",
          "body"
        ],
        "type": "object",
        "properties": {
          "send_from_org_email_id": {
            "description": "Boolean to trigger the email from the organization's email address.",
            "type": "boolean",
            "example": true
          },
          "from_address_id": {
            "description": "ID of From Address of the Email Address",
            "type": "integer",
            "format": "int64",
            "example": "460000008392548"
          },
          "to_mail_ids": {
            "description": "Array of email address of the recipients.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "willsmith@bowmanfurniture.com"
            ]
          },
          "cc_mail_ids": {
            "description": "Array of email address of the recipients to be cced.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "peterparker@bowmanfurniture.com"
            ]
          },
          "bcc_mail_ids": {
            "description": "Array of email address of the recipients to be BCC ed.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "mark@safInstruments.com"
            ]
          },
          "subject": {
            "description": "Subject of the mail.",
            "type": "string",
            "example": "Purchase Order from Zillium Inc (PO #: PO-00001)"
          },
          "body": {
            "description": "Body of the mail",
            "type": "string",
            "example": "Dear Bowman and Co, <br><br>The purchase order (PO-00001) is attached with this email. <br><br>An overview of the purchase order is available below: <br>Purchase Order # : PO-00001 <br>Date : 10 Feb 2014 <br>Amount : $112.00(in USD) <br><br>Please go through it and confirm the order. We look forward to working with you again<br><br><br><br>Regards<br><br>Zillium Inc<br><br>"
          },
          "mail_documents": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "document_id": {
                  "description": "ID of the Document",
                  "type": "integer",
                  "format": "int64"
                },
                "file_name": {
                  "description": "Name of the attached document file. Includes file extension and represents the original filename as uploaded. Used for display purposes and file identification in the system.",
                  "type": "string"
                }
              }
            }
          }
        }
      }
    }
  }
}
