http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "to_mail_ids",
          "subject",
          "body"
        ],
        "type": "object",
        "properties": {
          "send_from_org_email_id": {
            "description": "Boolean to trigger the email from the organization's email address",
            "type": "boolean",
            "example": false
          },
          "from_address_id": {
            "description": "From email address id",
            "type": "string",
            "example": "2000000011993"
          },
          "to_mail_ids": {
            "description": "Array of email address of the recipients.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "vendor@example.com"
            ]
          },
          "cc_mail_ids": {
            "description": "Array of email address of the recipients to be CC'd.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "manager@example.com"
            ]
          },
          "subject": {
            "description": "Subject of the email",
            "type": "string",
            "example": "Vendor Payment Receipt from Your Company (Payment#: VP-00001)"
          },
          "body": {
            "description": "Body of the email",
            "type": "string",
            "example": "Dear Vendor,<br><br>Thank you for your services.<br><br>Please find attached the payment receipt for Payment#: VP-00001.<br><br>Payment Details:<br>Payment Number: VP-00001<br>Date: 2024-01-15<br>Amount: $1,500.00<br><br>Best regards,<br>Your Company"
          },
          "email_template_id": {
            "description": "Get the email content based on a specific email template. If this param is not inputted, then the content will be based on the email template associated with the vendor. If no template is associated with the vendor, then default template will be used.",
            "type": "string"
          }
        }
      }
    }
  }
}
