http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "contact_name"
        ],
        "type": "object",
        "properties": {
          "contact_name": {
            "description": "Display name for the contact. It is used for searching and displaying contacts.",
            "type": "string",
            "example": "Bowman and Co"
          },
          "company_name": {
            "description": "Legal or registered contact's company name. Used for legal documents and formal communications. Max-length [200].",
            "type": "string",
            "example": "Bowman and Co"
          },
          "payment_terms": {
            "description": "Number of days allowed for payment after the invoice date.",
            "type": "integer",
            "example": 15
          },
          "payment_terms_label": {
            "description": "Human-readable label of payment terms displayed on invoices and other transactions. Max-length [200].",
            "type": "string",
            "example": "Net 15"
          },
          "contact_type": {
            "description": "Determines how the contact is treated in the system. Allowed values: <code>customer</code>, <code>vendor</code>.",
            "type": "string",
            "example": "customer"
          },
          "customer_sub_type": {
            "description": "<b>For Customer Only :</b> Additional classification for customers. Allowed values: <code>individual</code>, <code>business</code>.",
            "type": "string",
            "example": "business"
          },
          "currency_id": {
            "description": "Unique identifier for the currency assigned to this contact. The currency_id must correspond to a valid currency configuration in the system.",
            "type": "string",
            "example": 460000000000097
          },
          "opening_balances": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "location_id": {
                  "description": "Unique identifier for the business location associated with this opening balance.",
                  "type": "string",
                  "example": "460000000038080"
                },
                "exchange_rate": {
                  "description": "Currency exchange rate used to convert opening balance to organization's base currency.",
                  "type": "number",
                  "format": "double",
                  "example": 1
                },
                "opening_balance_amount": {
                  "description": "Initial balance amount for the contact at the time of creation.",
                  "type": "number",
                  "format": "double",
                  "example": 1200
                }
              }
            }
          },
          "credit_limit": {
            "description": "<b>For Customer Only :</b> The maximum credit amount that can be allowed for the customer. Once the customer's outstanding receivables reach this limit, the system will restrict the customer from creating any further transactions.",
            "type": "number",
            "format": "double",
            "example": 1000
          },
          "pricebook_id": {
            "description": "Pricebook id which is associated with the contact. Max-length [200].",
            "type": "string",
            "example": 460000000000123
          },
          "contact_number": {
            "description": "Contact number associated with the contact for internal tracking and identification purposes. Max-length [200].",
            "type": "string",
            "example": "CNT-001234"
          },
          "ignore_auto_number_generation": {
            "description": "Indicates whether the auto generation contact_number is ignored or not for the contact. Allowed values: <code>true</code>, <code>false</code>.",
            "type": "boolean",
            "example": false
          },
          "tags": {
            "description": "Filter all your reports based on the tag",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "tag_id": {
                  "description": "ID of the reporting tag",
                  "type": "string",
                  "example": 462000000009070
                },
                "tag_option_id": {
                  "description": "ID of the reporting tag's option",
                  "type": "string",
                  "example": 462000000002670
                }
              }
            }
          },
          "website": {
            "description": "Official website URL of the contact.",
            "type": "string",
            "example": "www.bowmanfurniture.com"
          },
          "owner_id": {
            "description": "<b>For Customer Only :</b> Unique identifier for the user assigned as the owner of the contact. This field specifies which user in the organization is responsible for managing the business relationship with this contact.",
            "type": "string",
            "example": 460000000016051
          },
          "custom_fields": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "index": {
                  "description": "Index of the custom field. It can hold any value from 1 to 10.",
                  "type": "integer",
                  "example": 1
                },
                "value": {
                  "description": "Value of the custom field.",
                  "type": "string",
                  "example": "GBGD078"
                },
                "label": {
                  "description": "Label of the custom field.",
                  "type": "string",
                  "example": "VAT ID"
                }
              }
            }
          },
          "billing_address": {
            "description": "Billing address information for the contact.",
            "type": "object",
            "properties": {
              "attention": {
                "description": "Attention for proper delivery and routing of business correspondence. Max-length [100].",
                "type": "string",
                "example": "Mr.John"
              },
              "address": {
                "description": "Street 1 address for the contact. Max-length [500].",
                "type": "string",
                "example": "4900 Hopyard Rd"
              },
              "street2": {
                "description": "Street 2 address for the contact. Max-length [255].",
                "type": "string",
                "example": "Suite 310"
              },
              "state_code": {
                "description": "State or province code for the address. Max-length [100].",
                "type": "string",
                "example": "CA"
              },
              "city": {
                "description": "City name for the address. Max-length [100].",
                "type": "string",
                "example": "Pleasanton"
              },
              "state": {
                "description": "State for the address. Max-length [100].",
                "type": "string",
                "example": "CA"
              },
              "zip": {
                "description": "Postal or ZIP code for the address. Max-length [50].",
                "type": "string",
                "example": 94588
              },
              "country": {
                "description": "Country name for the address. Max-length [100].",
                "type": "string",
                "example": "U.S.A"
              },
              "fax": {
                "description": "Fax number for the address. Max-length [50].",
                "type": "string",
                "example": "+1-925-924-9600"
              },
              "phone": {
                "description": "The contact phone number associated with the address. Max-length [50]",
                "type": "string",
                "example": "+1-925-921-9201"
              }
            }
          },
          "shipping_address": {
            "description": "Shipping address information for the contact.",
            "type": "object",
            "properties": {
              "attention": {
                "description": "Attention for proper delivery and routing of business correspondence. Max-length [100].",
                "type": "string",
                "example": "Mr.John"
              },
              "address": {
                "description": "Street 1 address for the contact. Max-length [500].",
                "type": "string",
                "example": "4900 Hopyard Rd"
              },
              "street2": {
                "description": "Street 2 address for the contact. Max-length [255].",
                "type": "string",
                "example": "Suite 310"
              },
              "state_code": {
                "description": "State or province code for the address. Max-length [100].",
                "type": "string",
                "example": "CA"
              },
              "city": {
                "description": "City name for the address. Max-length [100].",
                "type": "string",
                "example": "Pleasanton"
              },
              "state": {
                "description": "State for the address. Max-length [100].",
                "type": "string",
                "example": "CA"
              },
              "zip": {
                "description": "Postal or ZIP code for the address. Max-length [50].",
                "type": "string",
                "example": 94588
              },
              "country": {
                "description": "Country name for the address. Max-length [100].",
                "type": "string",
                "example": "U.S.A"
              },
              "fax": {
                "description": "Fax number for the address. Max-length [50].",
                "type": "string",
                "example": "+1-925-924-9600"
              },
              "phone": {
                "description": "The contact phone number associated with the address. Max-length [50]",
                "type": "string",
                "example": "+1-925-921-9201"
              }
            }
          },
          "contact_persons": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "contact_person_id": {
                  "description": "ID of the contact person. For an existing contact person, pass the <code>contact_person_id</code>.",
                  "type": "string",
                  "example": 460000000026051
                },
                "salutation": {
                  "description": "Salutation for the contact. Max-length [25]",
                  "type": "string",
                  "example": "Mr"
                },
                "first_name": {
                  "description": "First name of the contact person. Max-length [100].",
                  "type": "string",
                  "example": "Will"
                },
                "last_name": {
                  "description": "Last name of the contact person. Max-length [100].",
                  "type": "string",
                  "example": "Smith"
                },
                "email": {
                  "description": "Email address of the contact person for business communications. Max-length [100].",
                  "type": "string",
                  "example": "willsmith@bowmanfurniture.com"
                },
                "phone": {
                  "description": "Phone number of the contact person. Max-length [50].",
                  "type": "string",
                  "example": "+1-925-921-9201"
                },
                "mobile": {
                  "description": "Mobile number of the contact person. Max-length [50].",
                  "type": "string",
                  "example": "+1-4054439562"
                },
                "designation": {
                  "description": "Designation of the contact person within the contact's company. Max-length [100].",
                  "type": "string",
                  "example": "Sales Executive"
                },
                "department": {
                  "description": "Department of the contact person within the contact's company. Max-length [50].",
                  "type": "string",
                  "example": "Sales and Marketing"
                },
                "skype": {
                  "description": "Skype username of the contact person. Max-length [50].",
                  "type": "string",
                  "example": "Zoho"
                },
                "is_primary_contact": {
                  "description": "Indicates whether this contact person is the primary contact. Only one per contact. Allowed values: <code>true</code> and <code>false</code>.",
                  "type": "boolean",
                  "example": true
                },
                "enable_portal": {
                  "description": "Indicates whether the contact person should have access to the client portal. Allowed values: <code>true</code> and <code>false</code>.",
                  "type": "boolean",
                  "example": true
                },
                "communication_preference": {
                  "description": "Preferred modes of communication for the contact person.",
                  "type": "object",
                  "properties": {
                    "is_sms_enabled": {
                      "description": "Whether SMS text messages are allowed for the contact person.",
                      "type": "boolean",
                      "example": false,
                      "x-node_available_in": [
                        "SMS integration"
                      ]
                    },
                    "is_whatsapp_enabled": {
                      "description": "Whether WhatsApp messaging is allowed for the contact person.",
                      "type": "boolean",
                      "example": false,
                      "x-node_available_in": [
                        "WhatsApp integration"
                      ]
                    }
                  }
                }
              }
            }
          },
          "default_templates": {
            "type": "object",
            "properties": {
              "invoice_template_id": {
                "description": "Default invoice template id used for this customer while creating invoice.",
                "type": "string",
                "example": 460000000052069
              },
              "estimate_template_id": {
                "description": "Default estimate template id used for this customer while creating estimate.",
                "type": "string",
                "example": 460000000000179
              },
              "creditnote_template_id": {
                "description": "Default credit note template id used for this customer while creating credit note.",
                "type": "string",
                "example": 460000000000211
              },
              "purchaseorder_template_id": {
                "description": "Default purchase order template id used for this vendor while creating purchase order.",
                "type": "string",
                "example": 460000000000213
              },
              "salesorder_template_id": {
                "description": "Default sales order template id used for this customer while creating sales order.",
                "type": "string",
                "example": 460000000000214
              },
              "retainerinvoice_template_id": {
                "description": "Default retainer invoice template id used for this customer while creating retainer invoice.",
                "type": "string",
                "example": 460000000000215
              },
              "paymentthankyou_template_id": {
                "description": "Default payment thank you template id used for this contact while sending payment acknowledgment.",
                "type": "string",
                "example": 460000000000216
              },
              "retainerinvoice_paymentthankyou_template_id": {
                "description": "Default retainer invoice payment thank you template id used for this customer while sending payment acknowledgment for retainer invoices.",
                "type": "string",
                "example": 460000000000217
              },
              "invoice_email_template_id": {
                "description": "Default invoice email template id used for this customer while sending invoices.",
                "type": "string",
                "example": 460000000052071
              },
              "estimate_email_template_id": {
                "description": "Default estimate email template id used for this customer while sending estimates.",
                "type": "string",
                "example": 460000000052073
              },
              "creditnote_email_template_id": {
                "description": "Default credit note email template id used for this customer while sending credit notes.",
                "type": "string",
                "example": 460000000052075
              },
              "purchaseorder_email_template_id": {
                "description": "Default purchase order email template id used for this vendor while sending purchase order.",
                "type": "string",
                "example": 460000000000218
              },
              "salesorder_email_template_id": {
                "description": "Default sales order email template id used for this customer while sending sales order.",
                "type": "string",
                "example": 460000000000219
              },
              "retainerinvoice_email_template_id": {
                "description": "Default retainer invoice email template id used for this contact while sending retainer invoice.",
                "type": "string",
                "example": 460000000000220
              },
              "paymentthankyou_email_template_id": {
                "description": "Default payment thank you email template id used for this contact while sending payment acknowledgment.",
                "type": "string",
                "example": 460000000000221
              },
              "retainerinvoice_paymentthankyou_email_template_id": {
                "description": "Default retainer invoice payment thank you email template id used for this customer while sending payment acknowledgment for retainer invoices.",
                "type": "string",
                "example": 460000000000222
              },
              "statement_template_id": {
                "description": "Default statement template id used for this contact while creating statement.",
                "type": "string",
                "example": 460000000000101
              },
              "payment_remittance_email_template_id": {
                "description": "Default payment remittance email template id used for this contact while sending payment remittances.",
                "type": "string",
                "example": 460000000000102
              },
              "bill_template_id": {
                "description": "Default bill template id used for this vendor while creating bill.",
                "type": "string",
                "example": 460000000000103
              }
            }
          },
          "notes": {
            "description": "Additional comments or notes about the contact.",
            "type": "string",
            "example": "Payment option : Through check"
          },
          "vat_reg_no": {
            "description": "<b>For UK Edition:</b> VAT Registration number of a contact with length should be between 2 and 12 characters.<br/> <b>For Avalara:</b> If you are doing sales in the European Union (EU) then provide VAT Registration Number of your customers here. This is used to calculate VAT for B2B sales, from Avalara.",
            "type": "string",
            "x-node_available_in": [
              "uk",
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "tax_reg_no": {
            "description": "<b>For GCC Edition:</b> 15 digit Tax Registration number of a contact with Tax treatment as <code>vat_registered</code>,<code>gcc_vat_registered</code>,<code>dz_vat_registered</code>.<br/><b>For Mexico Edition:</b> 12 digit Tax Registration number of a contact with Tax treatment as </br> <code>home_country_mexico</code>, <code>border_region_mexico</code>, <code>non_mexico</code>.</br> Consumers generic RFC: <code>XAXX010101000</code>, Overseas generic RFC: <code>XEXX010101000</code>.<br/><b>For Kenya Edition:</b> 11 digit Tax Registration number of a contact with Tax treatment as <code>vat_registered</code><br/><b>For SouthAfrica Edition:</b> 10 digit Tax Registration number of a contact with Tax treatment as <code>vat_registered</code>",
            "type": "string",
            "example": 12345678912345,
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "country_code": {
            "description": "<b>For UK Edition:</b> Two letter country code of a contact<br/> <b>For Avalara:</b> Two letter country code for the customer country, if your customer is not in US. Refer [AvaTax Codes for Countries and States][2].<br/> <b>For GCC Editions :</b> Two Letter country code for the GCC Country or the UAE emirate of the contact which will be considered as <b>place of supply</b>.<br/>Supported codes for UAE emirates are : </br>Abu Dhabi - <code>AB</code>,</br>Ajman - <code>AJ</code>,</br>Dubai - <code>DU</code>,</br>Fujairah - <code>FU</code>,</br>Ras al-Khaimah - <code>RA</code>,</br>Sharjah - <code>SH</code>,</br>Umm al-Quwain - <code>UM</code>.<br/>Supported codes for the GCC countries are : </br>United Arab Emirates - <code>AE</code>,</br>Saudi Arabia - <code>SA</code>,</br>Bahrain - <code>BH</code>,</br>Kuwait - <code>KW</code>,</br>Oman - <code>OM</code>,</br>Qatar - <code>QA</code>.",
            "type": "string",
            "x-node_available_in": [
              "uk",
              "gcc",
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "tax_treatment": {
            "description": "Tax treatment of the contact.<br/>Allowed Values: <code>vat_registered</code>,<code>vat_not_registered</code>,<code>gcc_vat_not_registered</code>,<code>gcc_vat_registered</code>,<code>non_gcc</code>,<code>dz_vat_registered</code> and <code>dz_vat_not_registered</code></br><code>home_country_mexico</code> (A business that is located within MX)</br><code>border_region_mexico</code> (A business that is located in the northern and southern border regions in MX)</br><code>non_mexico</code> (A business that is located outside MX).<br/><b>For Kenya Edition:</b> <code>vat_registered</code> ,<code>vat_not_registered</code> ,<code>non_kenya</code>(A business that is located outside Kenya).<br/><b>For SouthAfrica Edition:</b> <code>vat_registered</code>, <code>vat_not_registered</code>, <code>overseas</code>(A business that is located outside SouthAfrica).",
            "type": "string",
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_certificate_number": {
            "description": "Tax Exemption Certificate number is issued by the Kenya Revenue Authority (KRA) to organizations or individuals who qualify for tax exemption",
            "type": "string",
            "example": "KRAEXM0043310521",
            "x-node_available_in": [
              "ke"
            ],
            "x-node_unavailable_in": []
          },
          "tax_regime": {
            "description": "Tax Regime of the contact.Allowed Values: <code>general_legal_person</code>, <code>legal_entities_non_profit</code>, <code>resident_abroad</code>, <code>production_cooperative_societies</code>, <code>agricultural_livestock</code>, <code>optional_group_of_companies</code>, <code>coordinated</code>, <code>simplified_trust</code>, <code>wages_salaries_income</code>, <code>lease</code>, <code>property_disposal_acquisition</code>, <code>other_income</code>, <code>resident_abroad</code>, <code>divident_income</code>, <code>individual_business_professional</code>, <code>interest_income</code>, <code>income_obtaining_price</code>, <code>no_tax_obligation</code>, <code>tax_incorporation</code>, <code>income_through_technology_platform</code>, <code>simplified_trust</code>.",
            "type": "string",
            "example": "general_legal_person",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "legal_name": {
            "description": "Official legal name of the contact as registered with tax authorities in Mexico.",
            "type": "string",
            "example": "ESCUELA KEMPER URGATE",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "is_tds_registered": {
            "description": "Indicates whether the contact is registered for Tax Deducted at Source (TDS) in Mexico.",
            "type": "boolean",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "vat_treatment": {
            "description": "VAT treatment of the contact.Allowed Values: <br/><code>uk</code> (A business that is located in the UK.),<br/><code>eu_vat_registered</code> (A business that is reg for VAT and trade goods between Northern Ireland and EU. This node is available only for organizations enabled for NI protocal in VAT Settings.) and<br/><code>overseas</code> (A business that is located outside UK. Pre Brexit, this was split as <code>eu_vat_registered</code>, <code>eu_vat_not_registered</code> and <code>non_eu</code> ).",
            "type": "string",
            "x-node_available_in": [
              "uk"
            ],
            "x-node_unavailable_in": []
          },
          "place_of_contact": {
            "description": "State or union territory code where the contact is located for Indian tax purposes. (This node identifies the place of supply and source of supply when invoices/bills are raised for the customer/vendor respectively. This is not applicable for Overseas contacts)",
            "type": "string",
            "example": "TN",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "gst_no": {
            "description": "15-digit GST identification number of the contact as issued by the Indian tax authorities.",
            "type": "string",
            "example": "22AAAAA0000A1Z5",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "gst_treatment": {
            "description": "Choose whether the contact is GST registered/unregistered/consumer/overseas. Allowed values are <code> business_gst </code> , <code> business_none </code> , <code> overseas </code> , <code> consumer </code>.",
            "type": "string",
            "example": "business_gst",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "tax_authority_name": {
            "description": "Display name of the tax authority responsible for the customer's tax jurisdiction.",
            "type": "string"
          },
          "avatax_exempt_no": {
            "description": "Exemption certificate number of the customer.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_use_code": {
            "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule. Select from Avalara [standard codes][1] or enter a custom code.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_id": {
            "description": "Unique identifier for the tax exemption configuration assigned to this contact.",
            "type": "string",
            "example": 11149000000061054,
            "x-node_available_in": [
              "us",
              "ca",
              "au",
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_code": {
            "description": "Code identifier for the tax exemption assigned to the contact.",
            "type": "string",
            "x-node_available_in": [
              "us",
              "ca",
              "au",
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "tax_authority_id": {
            "description": "Unique identifier for the tax authority responsible for the customer's tax jurisdiction. Tax authority depends on the location of the customer. For example, if the customer is located in NY, then the tax authority is NY tax authority.",
            "type": "string",
            "example": 11149000000061052,
            "x-node_available_in": [
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "tax_id": {
            "description": "Unique identifier for the tax or tax group assigned to the contact. Available only when <code>is_taxable</code> is <code>true</code>.",
            "type": "string",
            "example": 11149000000061058,
            "x-node_available_in": [
              "in",
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "is_taxable": {
            "description": "Indicates whether the customer is subject to tax collection. Tax-related fields are only available when this field is set to true. Allowed values: <code>true</code> and <code>false</code>.",
            "type": "boolean",
            "example": true,
            "x-node_available_in": [
              "us",
              "ca",
              "au",
              "in",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "facebook": {
            "description": "Facebook profile URL for the contact. Max-length [100].",
            "type": "string",
