http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "contact_id",
          "first_name"
        ],
        "type": "object",
        "properties": {
          "contact_id": {
            "description": "Contact id of the contact",
            "type": "string",
            "example": 460000000026049
          },
          "salutation": {
            "description": "Salutation for the contact. Max-length [25]",
            "type": "string",
            "example": "Mr"
          },
          "first_name": {
            "description": "First name of the contact person. Max-length [100]",
            "type": "string",
            "example": "Will"
          },
          "last_name": {
            "description": "Last name of the contact person. Max-length [100]",
            "type": "string",
            "example": "Smith"
          },
          "email": {
            "description": "Email address of the contact person.  Max-length [100]",
            "type": "string",
            "example": "willsmith@bowmanfurniture.com"
          },
          "phone": {
            "description": "Max-length [50]",
            "type": "string",
            "example": "+1-925-921-9201"
          },
          "mobile": {
            "description": "Max-length [50]",
            "type": "string",
            "example": "+1-4054439562"
          },
          "skype": {
            "description": "skype address. Max-length [50]",
            "type": "string",
            "example": "zoho"
          },
          "designation": {
            "description": "designation of a person. Max-length [50]",
            "type": "string",
            "example": "Sales Engineer"
          },
          "department": {
            "description": "department on which a person belongs. Max-length [50]",
            "type": "string",
            "example": "Sales"
          },
          "enable_portal": {
            "description": "option to enable the portal access. allowed values <code>true</code>,<code>false</code>",
            "type": "boolean",
            "example": true
          },
          "is_primary_contact": {
            "description": "To mark contact person as primary for contact",
            "type": "boolean",
            "example": true
          },
          "communication_preference": {
            "description": "Preferred modes of communication for the contact person.",
            "type": "object",
            "properties": {
              "is_whatsapp_enabled": {
                "description": "Used to check if WhatsApp communication preference is enabled for the contact person.",
                "type": "boolean",
                "example": true,
                "x-node_available_in": [
                  "WhatsApp integration"
                ]
              }
            }
          }
        }
      }
    }
  }
}
