http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "vendor_id",
          "line_items"
        ],
        "type": "object",
        "properties": {
          "vendor_id": {
            "description": "Unique identifier of the vendor for whom the purchase order is being created. This value is mandatory to associate the purchase order with a specific vendor in your organization. You can obtain the vendor ID by listing all vendors using the contacts API. Ensure the vendor exists before referencing their ID here.",
            "type": "string",
            "example": "460000000026049"
          },
          "currency_id": {
            "description": "Unique identifier of the currency for the purchase order. This value is mandatory to specify the currency in which the purchase order amounts will be displayed and processed. You can obtain the currency ID by listing all available currencies using the GET /settings/currencies API. Ensure the currency exists and is properly configured in your organization before referencing its ID here.",
            "type": "string",
            "example": "460000000000099"
          },
          "contact_persons_associated": {
            "description": "Contact Persons associated with the transaction.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "contact_person_id": {
                  "description": "Unique ID of the Contact Person",
                  "type": "integer",
                  "format": "int64",
                  "example": 460000000031001
                },
                "communication_preference": {
                  "description": "Preferred modes of communication for the contact person at transaction level.",
                  "type": "object",
                  "properties": {
                    "is_email_enabled": {
                      "description": "Used to check if Email communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true
                    },
                    "is_whatsapp_enabled": {
                      "description": "Used to check if WhatsApp communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true,
                      "x-node_available_in": [
                        "WhatsApp integration"
                      ]
                    }
                  }
                }
              }
            }
          },
          "purchaseorder_number": {
            "description": "Unique identifier for the purchase order. This field is mandatory when auto number generation is disabled for your organization. If auto numbering is enabled, this field can be left empty as the system will automatically generate a sequential purchase order number. When manually specifying a number, ensure it follows your organization's numbering convention and is unique across all purchase orders.",
            "type": "string",
            "example": "PO-00001"
          },
          "gst_treatment": {
            "description": "Choose whether the contact is GST registered/unregistered/consumer/overseas. Allowed values are <code> business_gst </code> , <code> business_none </code> , <code> overseas </code> , <code> consumer </code>.",
            "type": "string",
            "example": "business_gst",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "tax_treatment": {
            "description": "VAT treatment for the purchase order. Choose whether the vendor falls under: <code>vat_registered</code>,<code>vat_not_registered</code>,<code>gcc_vat_not_registered</code>,<code>gcc_vat_registered</code>,<code>non_gcc</code>.</br><code>dz_vat_registered</code> and <code>dz_vat_not_registered</code> are supported only for <b>UAE</b>.</b></br><code>home_country_mexico</code>,<code>border_region_mexico</code>,<code>non_mexico</code> supported only for <b>MX</b>.<br/><b>For Kenya Edition:</b> <code>vat_registered</code> ,<code>vat_not_registered</code> ,<code>non_kenya</code>(A business that is located outside Kenya).<br/><b>For SouthAfrica Edition:</b> <code>vat_registered</code>, <code>vat_not_registered</code>, <code>overseas</code>(A business that is located outside SouthAfrica).",
            "example": "vat_registered",
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "gst_no": {
            "description": "15 digit GST identification number of the vendor.",
            "type": "string",
            "example": "22AAAAA0000A1Z5",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "source_of_supply": {
            "description": "Place from where the goods/services are supplied. (If not given, <code>place of contact</code> given for the contact will be taken)",
            "type": "string",
            "example": "AP",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "destination_of_supply": {
            "description": "Place where the goods/services are supplied to. (If not given, organisation's home state will be taken)",
            "type": "string",
            "example": "TN",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "place_of_supply": {
            "description": "The place of supply is where a transaction is considered to have occurred for VAT purposes. For the supply of goods, the place of supply is the location of the goods when the supply occurs. For the supply of services, the place of supply should be where the supplier is established. (If not given, <code>place of contact</code> given for the contact will be taken)<br/>Supported codes for UAE emirates are : </br>Abu Dhabi - <code>AB</code>,</br>Ajman - <code>AJ</code>,</br>Dubai - <code>DU</code>,</br>Fujairah - <code>FU</code>,</br>Ras al-Khaimah - <code>RA</code>,</br>Sharjah - <code>SH</code>,</br>Umm al-Quwain - <code>UM</code>.<br/>Supported codes for the GCC countries are : </br>United Arab Emirates - <code>AE</code>,</br>Saudi Arabia - <code>SA</code>,</br>Bahrain - <code>BH</code>,</br>Kuwait - <code>KW</code>,</br>Oman - <code>OM</code>,</br>Qatar - <code>QA</code>.",
            "type": "string",
            "example": "DU",
            "x-node_available_in": [
              "gcc"
            ],
            "x-node_unavailable_in": []
          },
          "pricebook_id": {
            "description": "Unique identifier of the pricebook to be used for this purchase order. The pricebook contains predefined pricing information for items and services. This field is optional and if not specified, the default pricebook will be used. You can obtain the pricebook ID by listing all available pricebooks in your organization. Ensure the pricebook exists and contains the relevant pricing information before referencing its ID here.",
            "type": "string",
            "example": 460000000026089
          },
          "reference_number": {
            "description": "External reference number or identifier for the purchase order. This field is optional and can be used to store additional reference information such as vendor order numbers, internal tracking codes, or any other external system identifiers. This reference number will be displayed on the purchase order document and can be used for cross-referencing with external systems or internal tracking purposes.",
            "type": "string",
            "example": "ER/0034"
          },
          "discount": {
            "description": "The discount amount or percentage to be applied to the purchase order total. This can be specified as a fixed amount or percentage value. Discounts are typically offered by vendors for bulk purchases, early payments, or promotional purposes. The discount reduces the total amount payable and affects the final purchase order total.",
            "type": "string",
            "example": "10"
          },
          "discount_account_id": {
            "description": "Unique identifier of the accounting account where the discount amount will be recorded. This account is used to track and categorize discount expenses in your general ledger. The discount account should be configured as an expense or contra-revenue account to properly reflect the impact of discounts on your financial statements.",
            "type": "string",
            "example": "460000000011105"
          },
          "is_discount_before_tax": {
            "description": "Boolean flag that determines whether the discount is applied before or after tax calculations. When set to true, the discount is applied to the subtotal before tax is calculated, resulting in tax being calculated on the discounted amount. When set to false, the discount is applied after tax calculations, which may affect the final total differently depending on your tax configuration.",
            "type": "boolean",
            "example": true
          },
          "billing_address_id": {
            "description": "Unique identifier of the billing address to be used for this purchase order. The billing address determines where invoices and related documents will be sent. This field is optional and if not specified, the default billing address from your organization settings will be used. You can obtain the billing address ID by listing all available addresses in your organization. Ensure the address exists and contains the correct billing information before referencing its ID here.",
            "type": "integer",
            "format": "int64",
            "example": "460000000017491"
          },
          "crm_owner_id": {
            "description": "Unique identifier of the CRM owner assigned to this purchase order. This field links the purchase order to a specific CRM user who is responsible for managing the vendor relationship and overseeing the procurement process. The CRM owner can track, follow up, and manage all aspects of this purchase order within the CRM system.",
            "type": "string"
          },
          "crm_custom_reference_id": {
            "description": "ID of CRM Custom Reference",
            "type": "integer",
            "format": "int64"
          },
          "template_id": {
            "description": "Unique identifier of the PDF template linked to the purchase order. Obtain this value from the `template_id` field in the response of the GET `/purchaseorders/templates` API for your organization. Determines which template is used for the purchase order PDF document, controlling the layout, formatting, and visual appearance of the generated purchase order.",
            "type": "string",
            "example": "460000000011003"
          },
          "date": {
            "description": "The date when the purchase order was created or issued. This is the official date that appears on the purchase order document and is used for record-keeping, reporting, and tracking purposes. The date should be in YYYY-MM-DD format and typically represents when the purchase order was authorized and sent to the vendor.",
            "type": "string",
            "example": "2014-02-10"
          },
          "delivery_date": {
            "description": "The expected delivery date when the goods or services ordered in this purchase order should be received. This date is used for planning, scheduling, and tracking deliveries. It helps in managing inventory, coordinating with vendors, and ensuring timely receipt of ordered items. The date should be in YYYY-MM-DD format and is typically communicated to the vendor as part of the purchase order terms.",
            "type": "string",
            "example": "2014-02-10"
          },
          "due_date": {
            "description": "The expected delivery date when the goods or services ordered in this purchase order should be received. This date is used for planning, scheduling, and tracking deliveries. It helps in managing inventory, coordinating with vendors, and ensuring timely receipt of ordered items. The date should be in YYYY-MM-DD format.",
            "type": "string"
          },
          "exchange_rate": {
            "description": "The currency exchange rate used to convert the purchase order amounts from the vendor's currency to your organization's base currency. This rate is applied when the purchase order is created in a foreign currency. A value of 1 indicates the same currency as your base currency. This rate affects all monetary calculations and reporting for this purchase order.",
            "type": "number",
            "format": "double",
            "example": 1
          },
          "is_inclusive_tax": {
            "description": "Boolean flag that specifies whether the line item rates include or exclude tax amounts. When set to true, the specified rates are inclusive of tax, meaning the tax amount is already included in the rate. When set to false, the rates are exclusive of tax, and tax will be calculated and added separately. This setting affects how tax calculations are performed and displayed on the purchase order.",
            "type": "boolean",
            "example": false,
            "x-node_available_in": [],
            "x-node_unavailable_in": [
              "us"
            ]
          },
          "notes": {
            "description": "Additional information, instructions, or comments to be displayed on the purchase order document. This field is typically used for delivery instructions, special requirements, vendor communications, or any other information relevant to the purchase order. The notes appear on the purchase order PDF and are visible to the vendor.",
            "type": "string",
            "example": "Please deliver as soon as possible."
          },
          "notes_default": {
            "description": "Default notes for purchase order",
            "type": "string"
          },
          "terms": {
            "description": "Terms and conditions text to be displayed on the purchase order document. This field is used to specify payment terms, delivery conditions, return policies, warranty information, or any other legal or business terms that apply to this transaction. The terms appear on the purchase order PDF and form part of the contractual agreement with the vendor.",
            "type": "string",
            "example": "Thanks for your business."
          },
          "terms_default": {
            "description": "Default terms for Purchase Orders",
            "type": "string"
          },
          "ship_via": {
            "description": "Shipment Preference",
            "type": "string"
          },
          "delivery_org_address_id": {
            "description": "Unique identifier of the organization address where the goods or services from this purchase order should be delivered. This field specifies the delivery location within your organization, such as a warehouse, office, or branch location. The address details are retrieved from your organization's address book and displayed on the purchase order for vendor reference.",
            "type": "string"
          },
          "delivery_customer_id": {
            "description": "Unique identifier of the customer for whom the goods or services in this purchase order are being procured. This field is used when the purchase order is created on behalf of a customer, such as in drop-shipping scenarios or when procuring items for specific customer projects. The customer information may be displayed on the purchase order for vendor reference.",
            "type": "string"
          },
          "attention": {
            "description": "The name or designation of the specific person who should be contacted or notified regarding the delivery of goods or services from this purchase order. This field is typically used to specify the primary contact person at the delivery location, such as a warehouse manager, office administrator, or project coordinator who will receive and process the delivered items.",
            "type": "string"
          },
          "vat_treatment": {
            "description": "(Optional) VAT treatment for the purchase order. VAT treatment denotes the location of the vendor, if the vendor resides in UK then the VAT treatment is <code>uk</code>. If the vendor is in an EU country & VAT registered, you are resides in Northen Ireland and purchasing Goods then his VAT treatment is <code>eu_vat_registered</code> and if he resides outside the UK then his VAT treatment is <code>overseas</code>(For Pre Brexit, this can be split as <code>eu_vat_registered</code>, <code>eu_vat_not_registered</code> and <code>non_eu</code>).",
            "type": "string",
            "x-node_available_in": [
              "uk"
            ],
            "x-node_unavailable_in": []
          },
          "is_update_customer": {
            "description": "Check if customer should be updated",
            "type": "string"
          },
          "salesorder_id": {
            "description": "Unique identifier of the sales order that is linked to this purchase order. This field establishes a connection between the sales order and purchase order, typically used in drop-shipping scenarios where you need to procure items to fulfill a customer's sales order. The linked sales order information may be displayed on the purchase order for vendor reference and internal tracking purposes.",
            "type": "integer",
            "format": "int64",
            "example": "460000124728314"
          },
          "location_id": {
            "description": "Unique identifier of the business location or branch where this purchase order is being created and managed. This field is used to associate the purchase order with a specific location within your organization, enabling location-based reporting, inventory tracking, and multi-location business operations. The location information affects how the purchase order is processed and where inventory is received.",
            "type": "string",
            "example": "460000000038080"
          },
          "line_items": {
            "description": "Line items of purchase order.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "line_item_id": {
                  "description": "ID of the line item. Mandatory if the existing line item has to be updated. If empty, a new line item will be created.",
                  "type": "string",
                  "example": "460000000074009"
                },
                "item_id": {
                  "description": "Unique identifier of the item or service in the line item. Mandatory field that links to the item catalog. Determines item details, pricing, and inventory tracking. Obtain from GET /items API.",
                  "type": "string",
                  "example": "460000000027009"
                },
                "account_id": {
                  "description": "Unique identifier of the accounting account for the line item. Mandatory for non-inventory items/services. Determines expense categorization in general ledger.",
                  "type": "string",
                  "example": "460000000074003"
                },
                "name": {
                  "description": "Display name of the line item on the purchase order. Auto-populated from item catalog when item_id is specified. Can be customized. Used for vendor reference and documentation.",
                  "type": "string",
                  "example": "Hard Drive"
                },
                "description": {
                  "description": "Additional details, specifications, or notes about the line item. Can include technical specs, requirements, delivery instructions. Appears on purchase order document for vendor reference.",
                  "type": "string"
                },
                "unit": {
                  "description": "Unit of measurement for the quantity (e.g., Nos, kgs, hours, meters). Should match vendor pricing structure. Used for quantity calculations and pricing.",
                  "type": "string",
                  "example": "Nos"
                },
                "location_id": {
                  "description": "Unique identifier of the business location or branch where this purchase order is being created and managed. This field is used to associate the purchase order with a specific location within your organization, enabling location-based reporting, inventory tracking, and multi-location business operations. The location information affects how the purchase order is processed and where inventory is received.",
                  "type": "string",
                  "example": "460000000038080"
                },
                "hsn_or_sac": {
                  "description": "HSN or SAC Code for the Item",
                  "type": "string",
                  "x-node_available_in": [
                    "in",
                    "ke",
                    "za"
                  ],
                  "x-node_unavailable_in": []
                },
                "reverse_charge_tax_id": {
                  "description": "Unique identifier of the reverse charge tax for the line item. Used when buyer is responsible for paying tax instead of vendor. Applicable for unregistered vendors or specific tax regulations. Must reference valid reverse charge tax configuration.",
                  "type": "string",
                  "example": 460000000026068
                },
                "rate": {
                  "description": "Unit price per item/service. Used to calculate total line item amount. Must be in purchase order currency. Tax inclusion depends on is_inclusive_tax setting. Affects pricing calculations and vendor billing.",
                  "type": "number",
                  "format": "double",
                  "example": 112
                },
                "quantity": {
                  "description": "Number of units being ordered. Used with rate to calculate total line item amount. Must match unit of measurement. Represents actual order quantity for delivery.",
                  "type": "string",
                  "example": 1
                },
                "item_order": {
                  "description": "Sequential order of line item within purchase order. Determines display order on documents and reports. Typically numbered sequentially from 0 or 1. Maintains consistency across related documents.",
                  "type": "integer",
                  "example": 0
                },
                "tax_id": {
                  "description": "Unique identifier of the tax for the line item. Determines tax rate, name, and treatment for calculations. Used in tax amount computation and display on purchase order. Obtain from taxes API.",
                  "type": "string"
                },
                "tds_tax_id": {
                  "type": "string",
                  "example": 90300000087370,
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_treatment_code": {
                  "description": "Specify reason for using out of scope.</br>Supported values for <b>UAE</b> are <code>uae_same_tax_group</code>, <code>uae_reimbursed_expense</code> and <code>uae_others</code>.</br> Supported values for <b>Bahrain</b> are <code>bahrain_same_tax_group</code>, <code>bahrain_transfer_of_concern</code>, <code>bahrain_disbursement</code>, <code>bahrain_head_to_branch_transaction</code>, <code>bahrain_warranty_repair_services</code> and <code>bahrain_others</code>.</br> Supported values for <b>KSA</b> are <code>ksa_reimbursed_expense</code>.",
                  "type": "string",
                  "example": "uae_others",
                  "x-node_available_in": [
                    "gcc"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_exemption_code": {
                  "description": "Code of the Tax Exemption",
                  "type": "string",
                  "x-node_available_in": [
                    "in",
                    "au",
                    "ca",
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_exemption_id": {
                  "description": "ID of the Tax Exemption",
                  "type": "string",
                  "x-node_available_in": [
                    "in",
                    "au",
                    "ca"
                  ],
                  "x-node_unavailable_in": []
                },
                "acquisition_vat_id": {
                  "description": "ID of the VAT Acquistion",
                  "type": "string",
                  "x-node_available_in": [
                    "uk",
                    "eu"
                  ],
                  "x-node_unavailable_in": []
                },
                "reverse_charge_vat_id": {
                  "description": "ID of the Reverse Charge",
                  "type": "string",
                  "x-node_available_in": [
                    "uk",
                    "eu"
                  ],
                  "x-node_unavailable_in": []
                },
                "item_custom_fields": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "index": {
                        "description": "Index of the Custom Field",
                        "type": "integer",
                        "example": 1
                      },
                      "value": {
                        "description": "Value of the Custom Field",
                        "type": "string",
                        "example": "Normal"
                      }
                    }
                  }
                },
                "tags": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "tag_id": {
                        "description": "ID of the Tag",
                        "type": "integer",
                        "format": "int64"
                      },
                      "tag_option_id": {
                        "description": "Id of the Tag Option",
                        "type": "integer",
                        "format": "int64"
                      }
                    }
                  }
                },
                "project_id": {
                  "description": "ID of the project",
                  "type": "string",
                  "example": 90300000087378
                }
              }
            }
          },
          "custom_fields": {
            "description": "Custom fields for purchase order.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "customfield_id": {
                  "type": "integer",
                  "format": "int64",
                  "example": "46000000012845"
                },
                "value": {
                  "description": "Value of the Custom Field",
                  "type": "string",
                  "example": "Normal"
                }
              }
            }
          },
          "documents": {
            "description": "Array of documents attached to the purchase order.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "document_id": {
                  "description": "ID of the Document",
                  "type": "integer",
                  "format": "int64"
                },
                "file_name": {
                  "description": "Name of the attached document file. Includes file extension and represents the original filename as uploaded. Used for display purposes and file identification in the system.",
                  "type": "string"
                }
              }
            }
          }
        }
      }
    }
  }
}
