# Mispar Siduri (ordinal gematria)

ORDINAL = {
    "א": 1,  "ב": 2,  "ג": 3,  "ד": 4,  "ה": 5,
    "ו": 6,  "ז": 7,  "ח": 8,  "ט": 9,
    "י": 10, "כ": 11, "ך": 11, "ל": 12,
    "מ": 13, "ם": 13, "נ": 14, "ן": 14,
    "ס": 15, "ע": 16, "פ": 17, "ף": 17,
    "צ": 18, "ץ": 18,
    "ק": 19, "ר": 20, "ש": 21, "ת": 22
}

def value(text: str) -> int:
    return sum(ORDINAL.get(ch, 0) for ch in text)