  {% if match | length > 4 %}
    {{ match[:3] | join(", ") }} en nog {{ (match | length - 3) }}
  {% else %}
    {%- for name in match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} en {% endif -%}
      {{ name }}
    {%- endfor -%}
  {% endif %}
{% endif %}

