    def _handle_event(
        self,
        event: Dict[str, Any],
        ans_multiple: ToolMultipleResult,
        logs: List[Dict[str, Any]],
        session_id: str
    ):
        # 角色：USER、THINK、TOOL、AI
        role = event.get('role', '')
        tool_name = event.get('tool_name', '')
        answer = event.get('answer', '')
        input_message = event.get('input', '')
        if role in ['AI']:
            # 最终结果
            if logs and logs[-1]['tool_name'] == ToolName.from_sailor.value:
                ans_multiple.text = logs[-1]['result'].get('text', [])
            else:
                ans_multiple.text = [answer]
        elif role in ['TOOL']:
            try:
                if isinstance(answer, str):
                    answer = json.loads(answer)
            except ValueError:
                logger.info("answer {} struct is wrong".format(answer))
            time = 0
            tokens = 0
            if isinstance(answer, dict):
                time = answer.get('time', 0)
                tokens = answer.get('tokens', 0)

            log = logs[-1]
            log['time'] = time
            log['tokens'] = tokens

            tool = self._tool_kits.get_tool(tool_name) if self._tool_kits else None
            if tool:
                if hasattr(tool, 'handle_result'):
                    handler = tool.handle_result
                if handler:
                    handler(log, ans_multiple)

        log = LogResult(
            tool_name=tool_name,
            tool_input=input_message if tool_name else '',
            thought=answer,
            result={}
        )
        logs.append(log.to_json())

        res = json.dumps({
            'ans': ans_multiple.to_json(),
            'session_id': session_id,
            'logs': logs
        }, ensure_ascii=False)
        return res

