# STANDARD IMPORTS =====================================================================================================

from typing import *

# THIRD PARTY IMPORTS ==================================================================================================

from manim import *

# MANIMERA IMPORTS =====================================================================================================

from manimera.components.logic_gates.disjunction.base import DisjunctionBase

# ======================================================================================================================
# NOR CLASS
# ======================================================================================================================


class NOR(DisjunctionBase):
    def __init__(self, input_ports: int = 2):
        super().__init__(input_ports=input_ports, exclusive=False, negated=True)

    def _evaluate(self) -> float:
        """
        Abstract method to evaluate the gate.
        """
        return 1 - max([self.input_port_signal_values[i].get_value() for i in range(self.input_ports_count)])


# ==================================================================================================================
# NOR CLASS END
# ==================================================================================================================
