# BUFFER GATE CLASS
# ======================================================================================================================


class BUFFER(UnaryBase):
    def __init__(self):
        super().__init__(input_ports=1, negated=False)

    def _evaluate(self) -> float:
        """
        Abstract method to evaluate the gate.
        """
