# 3. Teste uma operação simples
try:
    # Patch temporário para interceptar requests
    import requests

    original_get = requests.Session.get

    def patched_get(self, url, **kwargs):
        response = original_get(self, url, **kwargs)
        if response.status_code == 400:
            print('\n=== DEBUG DA RESPOSTA 400 ===')
            print(f'Status Code: {response.status_code}')
            print(f'Headers: {dict(response.headers)}')
            print(f'URL: {response.url}')
            try:
                json_data = response.json()
                print(f'JSON Response: {json_data}')
            except:
                print(f'Text Response: {response.text}')
            print('=== FIM DEBUG ===\n')
        return response

    requests.Session.get = patched_get

    # Exemplo: consultar faixas de nosso número (mais seguro para teste)
    resultado = client.cobranca.boleto.consultar_faixas_nosso_numero(
        numero_cliente=12345,  # Número cliente de teste
        codigo_modalidade=1,  # Modalidade simples
        quantidade=1,  # Quantidade mínima
        client_id=client_id,  # Usar o client_id do teste
    )
    print('✅ Consulta de boletos funcionando!')
    print(f'Resultado: {resultado}')
except Exception as e:
    print(f'❌ Erro na API: {e}')
    # Se for uma exceção HTTP, vamos ver o conteúdo da resposta
    if hasattr(e, 'response') and hasattr(e.response, 'text'):
        print(f'Status Code: {e.response.status_code}')
        print(f'Conteúdo da resposta: {e.response.text}')
    elif hasattr(e, 'args') and len(e.args) > 0:
        print(f'Detalhes do erro: {e.args}')

    # Vamos tentar extrair mais informações do erro
    import traceback

    traceback.print_exc()
